import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { useState, useEffect } from "react";
import { FaSpinner } from "react-icons/fa";
import { BiSkipNext } from "react-icons/bi";
import { MdModeEdit } from "react-icons/md";
import LoanIconButton from "../../../../Common/LoanIconButton";
import { ToastContainer, toast } from "react-toastify";
import CompanyModal from "../../../Common/CompanyModal";
import LoanTextField from "../../../../Common/LoanTextField";
import AddTermLoan from "./AddTermLoan";
import TermLoanService from "./TermLoanService";
const ViewTermLoan = ({
  isTermLoanModalOpen,
  handleTermLoanOpenModal,
  handleTermLoanCloseModal,
  termLoan,
  creditFacilityUtilizitionId,
}) => {
  const [termLoanErrorLoading, setTermLoanErrorLoading] = useState(false);
  const [deleteLoading, setDeleteLoading] = useState(false);
  const [paymentDueDate, setPaymentDueDate] = useState(new Date());
  const [actualPaymentDate, setActualPaymentDate] = useState(new Date());
  const [startDate, setStartDate] = useState(new Date());
  const [maturityDate, setMaturityDate] = useState(new Date());
  const [deleteTermLoanError, setTermLoanDeleteError] = useState(null);
  const [error, setError] = useState(null);
  const [isTermLoanEditModalOpen, setIsTermLoanEditModalOpen] = useState(false);
  
  function handleTermLoanEditOpenModal() {
    setIsTermLoanEditModalOpen(true);
  }
  function handleTermLoanEditCloseModal() {
    setIsTermLoanEditModalOpen(false);
  }
  function handleTermLoanEditOpenModal() {
    setIsTermLoanEditModalOpen(true);
  }
  function handleTermLoanEditCloseModal() {
    setIsTermLoanEditModalOpen(false);
  }
  const [termLoanId, setTermLoanId] = useState(null);
  const handleInputChangeTermLoan = (event) => {
    const { name, value } = event.target;
    setTermLoans({
      ...termLoans,
      [name]: value,
    });
  };
  const initialTermLoanState = {
    Id: null,
    ApprovedAmount: "",
    TotalCommitment: "",
    Commitment: "",
    StartDate: "",
    MaturityDate: "",
    RepaymentFrequencyId: "",
    TotalNumberOfScheduledPayment: "",
    ScheduledPayment: "",
    PaymentDueDate: "",
    DaysPastDue: "",
    ActualPaymentDate: "",
  };
  const [termLoans, setTermLoans] = useState(initialTermLoanState);
  const newTermLoan = () => {
    setTermLoans(initialTermLoanState);
    // setSubmitted(false);
  };
  const saveTermLoan = () => {
    var data = {
      ApprovedAmount: termLoans.ApprovedAmount,
      TotalCommitment: termLoans.TotalCommitment,
      Commitment: termLoans.Commitment,
      StartDate: termLoans.StartDate,
      MaturityDate: termLoans.MaturityDate,
      RepaymentFrequencyId: termLoans.RepaymentFrequencyId,
      TotalNumberOfScheduledPayment: termLoans.TotalNumberOfScheduledPayment,
      ScheduledPayment: termLoans.ScheduledPayment,
      PaymentDueDate: termLoans.PaymentDueDate,
      DaysPastDue: termLoans.DaysPastDue,
      ActualPaymentDate: termLoans.ActualPaymentDate,
    };
    TermLoanService.addTermLoan(data, creditFacilityUtilizitionId)
      .then((response) => {
        setTermLoans({
          Id: response.data.Id,
          ApprovedAmount: response.data.ApprovedAmount,
          TotalCommitment: response.data.TotalCommitment,
          Commitment: response.data.Commitment,
          StartDate: response.data.StartDate,
          MaturityDate: response.data.MaturityDate,
          RepaymentFrequencyId: response.data.RepaymentFrequencyId,
          TotalNumberOfScheduledPayment:
            response.data.TotalNumberOfScheduledPayment,
          ScheduledPayment: response.data.ScheduledPayment,
          PaymentDueDate: response.data.PaymentDueDate,
          DaysPastDue: response.data.DaysPastDue,
          ActualPaymentDate: response.data.ActualPaymentDate,
        });
        if (response.data.Code === 200) {
          setTermLoans(initialTermLoanState);
          console.log(response.data);
          // setHoldId(response.data.Result.Id);
          termLoan();
          toast.success("Added successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "termloan",
          });
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const handleDelete = (TermLoan) => {
    setDeleteLoading(true);
    const confirm = window.confirm(
      `Are you sure you want to delete ${TermLoan.Id}?`
    );
    if (confirm) {
      toast.success("Deleting", {
        position: toast.POSITION.TOP_RIGHT,
        toastId: "deletePreshipmentFacility",
      });
      TermLoanService.deleteTermLoanById(TermLoan.Id)
        .then(() => {
          TermLoan((previousData) =>
            previousData.filter((comp) => comp.Id !== TermLoan.Id)
          );
        })
        .catch((e) => {
          setTermLoanDeleteError(e);
          setDeleteLoading(false);
        });
    }
  };
  const [editTermLoan, setEditTermLoan] = useState({
    //  Id: editAddresAccountDetailId,
    ApprovedAmount: "",
    TotalCommitment: "",
    Commitment: "",
    StartDate: "",
    MaturityDate: "",
    RepaymentFrequencyId: "",
    TotalNumberOfScheduledPayment: "",
    ScheduledPayment: "",
    PaymentDueDate: "",
    DaysPastDue: "",
    ActualPaymentDate: "",
  });

  const handleInputChangeEditTermLoan = (event) => {
    const { name, value } = event.target;
    setEditTermLoan({
      ...editTermLoan,
      [name]: value,
    });
  };

  const getTermLoanId = (editTermLoanId) => {
    TermLoanService.getTermLoanById(editTermLoanId) //here is
      .then((res) => {
        setEditTermLoan({
          ...editTermLoan,
          Id: editTermLoanId,
          ApprovedAmount: res.data.Result.ApprovedAmount,
          TotalCommitment: res.data.Result.TotalCommitment,
          Commitment: res.data.Result.Commitment,
          StartDate: res.data.Result.StartDate,
          MaturityDate: res.data.Result.MaturityDate,
          RepaymentFrequencyId: res.data.Result.RepaymentFrequencyId,
          TotalNumberOfScheduledPayment:
            res.data.Result.TotalNumberOfScheduledPayment,
          ScheduledPayment: res.data.Result.ScheduledPayment,
          PaymentDueDate: res.data.Result.PaymentDueDate,
          DaysPastDue: res.data.Result.DaysPastDue,
          ActualPaymentDate: res.data.Result.ActualPaymentDate,
        });
      })
      .catch((err) => {
        if (err.response.data.StatusCode === 404) {
          console.log(err.response.data.Message);
        }
      });
  };
  const updateTermLoanById = (e) => {
    e.preventDefault();
    TermLoanService.updateTermLoanById(termLoanId, editTermLoan)
      .then((response) => {
        if (response.data.Code === 200) {
          termLoan();
          toast.success("Updated successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "updateCompany",
          });
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const [repaymentSchedule, setRepaymentSchedule] = useState([]);
  const getTermLoan = (Id) => {
    setTimeout(() => {
      TermLoanService.getAllTermLoanByUtilizationId(Id)
        .then((response) => {       
            setRepaymentSchedule(response.data.Result);           
        });
    }, 1000);
  };
  return (
    <div>
      <CompanyModal
        isOpen={isTermLoanModalOpen}
        onClose={handleTermLoanCloseModal}
        title=" Term loan"
      >
        <div className="px-4 py-3 ">
          <div className="w-flex flex-col bg-gray-800w-96 py-8 px-4 text-center">
            <div className="rounded-lg overflow-hidden overflow-x-scroll overflow-y-scroll">
              {/* <div className="bg-gray-100 flex items-center justify-center h-screen"> */}
              <div>
                <h4 className="text-lg font-semibold eading-tight p-2"></h4>
                <AddTermLoan
                  handleInputChangeTermLoan={handleInputChangeTermLoan}
                  saveTermLoan={saveTermLoan}
                  TermLoans={termLoans}
                  setTermLoans={setTermLoans}
                />
                <div>
                  {error && <p className="text-red-500">{error}</p>}
                  {!error && termLoanErrorLoading ? (
                    <span>
                      <FaSpinner
                        className="animate-spin rounded-full h-6 w-6 text-purple-600"
                        fontSize="small"
                        data-toggle="tooltip"
                        title="Click it to sign in"
                      />
                      Loading...
                    </span>
                  ) : (
                    !error &&
                    !termLoanErrorLoading && (
                      <div className="overflow-x-auto">
                        <ToastContainer />
                        <table className="min-w-full table table-bordered table-striped bg-purple-100">
                          <thead>
                            <tr className="bg-purple-200">
                              <th className="px-2 py-4"> Approved Amount</th>
                              <th className="px-2 py-4"> Total Commitment </th>
                              <th className="px-2 py-4"> Commitment</th>
                              <th className="px-2 py-4"> Start Date</th>
                              <th className="px-2 py-4"> Maturity Date </th>
                              <th className="px-2 py-4">
                                {" "}
                                Repayment Frequency{" "}
                              </th>
                              <th className="px-2 py-4">
                                {" "}
                                Total Number Of Scheduled Payment{" "}
                              </th>
                              <th className="px-2 py-4"> Scheduled Payment </th>
                              <th className="px-2 py-4"> Payment Due Date </th>
                              <th className="px-2 py-4"> Days Past Due </th>
                              <th className="px-2 py-4">
                                {" "}
                                Actual Payment Date{" "}
                              </th>
                              <th className="px-2 py-4"> Action </th>
                            </tr>
                          </thead>
                          <tbody>
                            {termLoan.length === 0 ? (
                              <p className="text-red-600 p-4">Data not found</p>
                            ) : (
                              termLoan.map((termloan, index) => (
                                <tr
                                  key={index}
                                  className="bg-white
                    border-b border-purple-200"
                                >
                                  <td className="p-2">
                                    {termloan.ApprovedAmount}
                                  </td>
                                  <td className="p-2">
                                    {termloan.TotalCommitment}
                                  </td>
                                  <td className="p-2">{termloan.Commitment}</td>
                                  <td className="p-2">{termloan.StartDate}</td>
                                  <td className="p-2">
                                    {termloan.MaturityDate}
                                  </td>
                                  <td className="p-2">
                                    {termloan.RepaymentFrequencyId}
                                  </td>
                                  <td className="p-2">
                                    {termloan.TotalNumberOfScheduledPayment}
                                  </td>{" "}
                                  <td className="p-2">
                                    {termloan.ScheduledPayment}
                                  </td>{" "}
                                  <td className="p-2">
                                    {termloan.PaymentDueDate}
                                  </td>{" "}
                                  <td className="p-2">
                                    {termloan.DaysPastDue}
                                  </td>{" "}
                                  <td className="p-2">
                                    {termloan.ActualPaymentDate}
                                  </td>
                                  <td className="p-2">
                                    <>
                                      <LoanIconButton
                                        style={{ color: "green" }}
                                        icon={<MdModeEdit />}
                                        text="Edit"
                                        onClick={() => {
                                          handleTermLoanEditOpenModal();
                                          getTermLoanId(termloan.Id);
                                          setTermLoanId(termloan.Id);
                                        }}
                                      />
                                    </>
                                    <>
                                      <LoanIconButton
                                        style={{ color: "red" }}
                                        text=" Delete"
                                        onClick={() => {
                                          handleDelete(termloan);
                                        }}
                                      >
                                        <ToastContainer />
                                      </LoanIconButton>
                                    </>
                                  </td>
                                </tr>
                              ))
                            )}
                          </tbody>
                        </table>
                      </div>
                    )
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </CompanyModal>
      <CompanyModal
        isOpen={isTermLoanEditModalOpen}
        onClose={handleTermLoanEditCloseModal}
        title="Modify account detail information"
      >
        <div>
          <ToastContainer />
          <form onSubmit={updateTermLoanById}>
            <div className="grid grid-cols-1 gap-4 submit-form">
              <div className="submit-form ">
                <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                  <LoanTextField
                    id="ApprovedAmount"
                    label="ApprovedAmount"
                    value={editTermLoan.ApprovedAmount}
                    onChange={handleInputChangeEditTermLoan}
                    name="ApprovedAmount"
                  />
                  <LoanTextField
                    id="TotalCommitment"
                    label="TotalCommitment"
                    value={editTermLoan.TotalCommitment}
                    onChange={handleInputChangeEditTermLoan}
                    name="TotalCommitment"
                  />
                  <LoanTextField
                    id="Commitment"
                    label="Commitment"
                    value={editTermLoan.Commitment}
                    onChange={handleInputChangeEditTermLoan}
                    name="Commitment"
                  />
                  <p>
                    Start Date
                    <input
                      id="StartDate"
                      name="StartDate"
                      label="StartDate"
                      placeholder="StartDate"
                      type="date"
                      value={
                        startDate.getFullYear().toString() +
                        "-" +
                        (startDate.getMonth() + 1).toString().padStart(2, 0) +
                        "-" +
                        startDate.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setStartDate(new Date(e.target.value));
                        editTermLoan.StartDate = startDate;
                      }}
                    />{" "}
                  </p>
                  <p>
                    Maturity Date
                    <input
                      id="MaturityDate"
                      name="MaturityDate"
                      label="MaturityDate"
                      placeholder="MaturityDate"
                      type="date"
                      value={
                        maturityDate.getFullYear().toString() +
                        "-" +
                        (maturityDate.getMonth() + 1)
                          .toString()
                          .padStart(2, 0) +
                        "-" +
                        maturityDate.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setMaturityDate(new Date(e.target.value));
                        editTermLoan.MaturityDate = maturityDate;
                      }}
                    />{" "}
                  </p>
                  {/* RepaymentFrequencyId */}
                  <FormControl variant="standard" sx={{ minWidth: 120 }}>
                    <InputLabel id="countrylbl" color="secondary">
                    Repayment Frequency{" "}
                    </InputLabel>
                    <Select
                      color="secondary"
                      onChange={(event) => {
                        editCreditFacilityUtilizition.LoanTypeId =
                          event.target.value;
                      }}
                      labelId="countrylbl"
                      id="countrySelect"
                      label="Country"
                    >
                      <MenuItem value="">
                        <em>None</em>
                      </MenuItem>
                      {repaymentSchedule.map((yourTermLoan) => (
                        <MenuItem
                          key={yourTermLoan.Id}
                          LoanTypeId={yourTermLoan.Id}
                          value={yourTermLoan.Id}
                        >
                          {yourTermLoan.Name}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>


                  <LoanTextField
                    id="TotalNumberOfScheduledPayment"
                    label="TotalNumberOfScheduledPayment"
                    value={editTermLoan.TotalNumberOfScheduledPayment}
                    onChange={handleInputChangeEditTermLoan}
                    name="TotalNumberOfScheduledPayment"
                  />
                  <LoanTextField
                    id="ScheduledPayment"
                    label="ScheduledPayment"
                    value={editTermLoan.ScheduledPayment}
                    onChange={handleInputChangeEditTermLoan}
                    name="ScheduledPayment"
                  />
                  <p>
                    Payment Due Date
                    <input
                      id="PaymentDueDate"
                      name="PaymentDueDate"
                      label="PaymentDueDate"
                      placeholder="PaymentDueDate"
                      type="date"
                      value={
                        paymentDueDate.getFullYear().toString() +
                        "-" +
                        (paymentDueDate.getMonth() + 1)
                          .toString()
                          .padStart(2, 0) +
                        "-" +
                        paymentDueDate.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setPaymentDueDate(new Date(e.target.value));
                        editTermLoan.PaymentDueDate = paymentDueDate;
                      }}
                    />{" "}
                  </p>
                  <LoanTextField
                    id="DaysPastDue"
                    label="DaysPastDue"
                    value={editTermLoan.DaysPastDue}
                    onChange={handleInputChangeEditTermLoan}
                    name="DaysPastDue"
                  />
                  <p>
                    Actual Payment Date
                    <input
                      id="ActualPaymentDate"
                      name="ActualPaymentDate"
                      label="ActualPaymentDate"
                      placeholder="ActualPaymentDate"
                      type="date"
                      value={
                        actualPaymentDate.getFullYear().toString() +
                        "-" +
                        (actualPaymentDate.getMonth() + 1)
                          .toString()
                          .padStart(2, 0) +
                        "-" +
                        actualPaymentDate.getDate().toString().padStart(2, 0)
                      }
                      onChange={(e) => {
                        setActualPaymentDate(new Date(e.target.value));
                        editTermLoan.ActualPaymentDate = actualPaymentDate;
                      }}
                    />{" "}
                  </p>
                </div>
                <div className="flex justify-center items-center">
                  <button
                    type="submit"
                    className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline "
                  >
                    Update
                  </button>
                </div>
              </div>
            </div>
          </form>
        </div>
      </CompanyModal>
    </div>
  );
};
export default ViewTermLoan;
