import { useState, useEffect } from "react";
import {
  BsArrowLeftShort,
  BsChevronDown,
  BsPlus,
  BsSearch,
} from "react-icons/bs";
import { AiFillEnvironment } from "react-icons/ai";
import { RiDashboardFill } from "react-icons/ri";
import { GrProjects } from "react-icons/gr";
import { GiProgression } from "react-icons/gi";
import { FcOrganization } from "react-icons/fc";
import cbeLogo from "../../Images/favicon.ico";
import Tooltips from "../../Tooltip/Tooltips";
import "./style.css";
import { Link, Navigate } from "react-router-dom";
import { useParams, useNavigate } from "react-router-dom";
import Basic from "../../Company/Basic";
import classNames from "classnames";
import { TextField } from "@mui/material";
import loanDataService from "../../Company/Service";
import { InputLabel, MenuItem, FormControl, Select } from "@mui/material";
// import { BsArrowLeftShort, BsSearch, BsChevronDown, BsPlus } from "react-icons/bs";
import CompanyModal from "./CompanyModal";
import { IconButton } from "@mui/material";
import { BsWindowPlus } from "react-icons/bs";
import { BiSkipNext } from "react-icons/bi";
import { RiSkipBackMiniFill } from "react-icons/ri";
import { useForm } from "react-hook-form";
import { useUploadFile } from "dex-react-file-upload";
const LeftNav = () => {
  let navigate = useNavigate();
  const { Id } = useParams();

  const initialCompanyState = {
    Id: null,
    Name: "",
    NoOfBranch: "",
    EstablishmentYear: "",
    TINNumber: "",
    TINCertificate: "",
    CompanyTypeID: "",
    EntityTypeID: "",
    VATNumber: "",
    VATCertificate: "",
  };
  //realted to martial status
  const [martailStatus, setMartailStatus] = useState();
  const [martailStatusHold, setMartailStatusHold] = useState();
  const [martailStatusHoldSetData, setMartailStatusHoldSetData] = useState([]);
  // debugger;

  //end maertial status
  //related to upload file
  const [selectedFiles, setSelectedFiles] = useState(undefined);
  // const [folderName, setFolderName] = useState("");
  // debugger;
  const [currentFile, setCurrentFile] = useState(undefined);
  const [progress, setProgress] = useState(0);
  const [message, setMessage] = useState("");
  const [fileInfos, setFileInfos] = useState([]);
  const [selectedPathLin, setSelectedPathLin] = useState();
  const [selectedPathTRN, setSelectedPathTRN] = useState();
  const [selectedPathLinEdit, setSelectedPathLinEdit] = useState();
  const [selectedPathTRNEdit, setSelectedPathTRNEdit] = useState();
  const [toggleTaxSoleInfo, setToggleTaxInfo] = useState(false);
  const [toggleGruantorSoleInfo, setToggleGruantorInfo] = useState(false);
  //related to registration number
  //NGO
  const [selectedPathPL, setSelectedPathPL] = useState();
  const [selectedPathLD, setSelectedPathLD] = useState();
  const [selectedPathPLEdit, setSelectedPathPLEdit] = useState();
  const [selectedPathLDEdit, setSelectedPathLDEdit] = useState();
  const [getNGOAndEmbassyInformation, setGetNGOAndEmbassyInformation] =
    useState();
  const [ngoAndEmbassyInformationId, setNGOAndEmbassyInformationId] =
    useState();
  const [NGOAndEmbassyInfoIdForLocal, setNGOAndEmbassyInfoIdForLocal] =
    useState();
  const [editNGOAndEmbassyInfo, setEditNGOAndEmbassyInfo] = useState({
    Id: ngoAndEmbassyInformationId,
    AuthRefNo: "",
    AuthDate: "",
    UploadPermisionLetter: selectedPathPLEdit,
    UploadParentCompanyLegalDocument: selectedPathLDEdit,
  });
  const selectFile2Edit = (event) => {
    setSelectedFiles(event.target.files);
  };
  const upload2Edit = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathPLEdit(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };

  const selectFile3Edit = (event) => {
    setSelectedFiles(event.target.files);
  };
  const upload3Edit = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathLDEdit(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //ENDE EDIT NGO
  //file upload NGO
  const selectFile2 = (event) => {
    setSelectedFiles(event.target.files);
  };
  const upload2 = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathPL(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };

  const selectFile3 = (event) => {
    setSelectedFiles(event.target.files);
  };
  const upload3 = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathLD(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  const initialNGOAndEmbassyInfoState = {
    Id: null,
    AuthRefNo: "",
    AuthDate: "",
    UploadPermisionLetter: "",
    UploadParentCompanyLegalDocument: "",
  };
  const getNGOAndEmbassyInformationBy = (ngoAndEmbassyInformationId) => {
    loanDataService
      .getNGOAndEmbassyInformationByNGOId(ngoAndEmbassyInformationId)
      .then((res) => {
        setEditNGOAndEmbassyInfo({
          ...editNGOAndEmbassyInfo,
          Id: ngoAndEmbassyInformationId,
          AuthRefNo: res.data.Result.AuthRefNo,
          AuthDate: res.data.Result.AuthDate,
          UploadPermisionLetter: res.data.Result.UploadPermisionLetter,
          UploadParentCompanyLegalDocument:
            res.data.Result.UploadParentCompanyLegalDocument,
        });
        // debugger;
        setNGOAndEmbassyInfoIdForLocal(ngoAndEmbassyInformationId);
      })
      .catch((err) => console.log(err));
  };

  const updateCompanyNGOAndEmbassyInformationById = () => {
    // event.preventDefault();
    loanDataService
      .updateCompanyNGOAndEmbassyInformation(
        NGOAndEmbassyInfoIdForLocal,
        editNGOAndEmbassyInfo
      )
      .then((response) => {
        // navigation("/company/1");
        console.log(response);
      })
      .catch((e) => {
        console.log(e);
      });
    // debugger;
  };
  useEffect(() => {
    updateCompanyNGOAndEmbassyInformationById();
  }, []);

  const [NGOAndEmbassyInfo, setNGOAndEmbassyInfo] = useState(
    initialNGOAndEmbassyInfoState
  );
  const initialNGOAndEmbassyState = {
    Id: null,
    AuthRefNo: "",
    AuthDate: "",
    UploadPermisionLetter: "",
    UploadParentCompanyLegalDocument: "",
  };
  const [NGOAndEmbassy, setNGOAndEmbassy] = useState(initialNGOAndEmbassyState);

  const [
    isCompanyNGOAndEmbassyInformationEditModalOpen,
    setIsCompanyNGOAndEmbassyInformationEditModalOpen,
  ] = useState(false);

  const [isCompanyNGOAndEmbassyModalOpen, setIsCompanyNGOAndEmbassyModalOpen] =
    useState(false);
  function handleGeneralCloseModal() {
    isCompanyNGOAndEmbassyModalOpen(false);
  }
  function handleCompanyNGOAndEmbassyInformationEditOpenModal() {
    setIsCompanyNGOAndEmbassyInformationEditModalOpen(true);
  }
  function handleCompanyNGOAndEmbassyInformationEditCloseModal() {
    setIsCompanyNGOAndEmbassyInformationEditModalOpen(false);
  }
  function handleCompanyNGOAndEmbassyOpenModal() {
    setIsCompanyNGOAndEmbassyModalOpen(true);
  }
  function handleCompanyNGOAndEmbassyCloseModal() {
    setIsCompanyNGOAndEmbassyModalOpen(false);
  }
  const newNGOAndEmbassyInfo = () => {
    setNGOAndEmbassyInfo(initialNGOAndEmbassyInfoState);
    setSubmitted(false);
  };
  const handleInputChangeNGOAndEmbassy = (event) => {
    const { name, value } = event.target;
    setNGOAndEmbassy({ ...NGOAndEmbassy, [name]: value });
  };
  const saveNGOAndEmbassy = () => {
    var data = {
      CompanyID: holdId,
      AuthRefNo: NGOAndEmbassy.AuthRefNo,
      AuthDate: NGOAndEmbassy.AuthDate,
      UploadPermisionLetter: selectedPathPL,
      UploadParentCompanyLegalDocument: selectedPathLD,
    };
    loanDataService
      .addNGOAndEmbassy(data, holdId)
      .then((response) => {
        setNGOAndEmbassy({
          Id: response.data.Id,
          //CompanyID: response.data.CompanyID,
          AuthRefNo: response.data.AuthRefNo,
          AuthDate: response.data.AuthDate,
          UploadPermisionLetter: response.data.UploadPermisionLetter,
          UploadParentCompanyLegalDocument:
            response.data.UploadParentCompanyLegalDocument,
        });
        setSubmitted(true);
        console.log(response.data);
        setHoldId(response.data.Result.Id);
      })
      .catch((e) => {
        console.log(e);
      });
    debugger;
  };
  useEffect(() => {
    const getCompanyNGOAndEmbassyInformationById = () => {
      setIsLoading(true);
      loanDataService
        .getCompanyNGOAndEmbassyInformation()
        .then((response) => {
          setGetNGOAndEmbassyInformation(response.data.Result);
          setNGOAndEmbassyInformationId(response.data.Result.Id);
        })
        .catch((e) => {
          console.log(e);
        });
      setIsLoading(false);
    };
    getCompanyNGOAndEmbassyInformationById();
  }, []);
  //end file upload NGO
  //Ngo end

  //edit Trade
  const selectFile1Edit = (event) => {
    setSelectedFiles(event.target.files);
  };
  const upload1Edit = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathTRNEdit(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //releted to trade licence
  const selectFileEdit = (event) => {
    setSelectedFiles(event.target.files);
  };
  const uploadEdit = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathLinEdit(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //end edit trade
  const selectFile1 = (event) => {
    setSelectedFiles(event.target.files);
  };
  const upload1 = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathTRN(response.data.Result);
        // debugger;
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //releted to trade licence
  const selectFile = (event) => {
    setSelectedFiles(event.target.files);
  };
  const upload = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathLin(response.data.Result);
        console.log("new path");
        console.log(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //end upload file
  //edit company
  const [activeStep, setActiveStep] = useState(0);
  const [open, setOpen] = useState(true);
  const [subMenuOpen, setSubMenuOpen] = useState(false);
  const [leftNav, setLeftNav] = useState();
  const [submitted, setSubmitted] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(-1);
  const [companyTypes, setCompanyTypes] = useState([]);
  const [entityTypes, setEntityTypes] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [companyId, setCompanyId] = useState();
  const [addressId, setAddressId] = useState();
  const [addressIdForLocal, setAddressIdForLocal] = useState();

  const [getAddress, setGetAddress] = useState();
  const [getContactInformation, setGetContactInformation] = useState();
  const [contactInformationId, setContactInformationId] = useState();
  const [getAccountInformation, setGetAccountInformation] = useState();
  const [getTradeInformation, setGetTradeInformation] = useState();
  const [
    getSoleAndPartinershipInformation,
    setGetSoleAndPartinershipInformation,
  ] = useState();
  const [getSoleTaxId, setGetSoleTaxId] = useState();
  // const refresh = () => window.location.reload(true);
  const [holdId, setHoldId] = useState([]);
  debugger;
  const [getSoleTax, setGetSoleTax] = useState();
  const [getSoleGrauntor, setGetSoleGrauntor] = useState();
  // debugger;
  const [accountInformationId, setAccountInformationId] = useState();
  const [tradeInformationId, setTradeInformationId] = useState();
  const [
    soleAndPartinershipInformationId,
    setSoleAndPartinershipInformationId,
  ] = useState();
  const [taxInformationId, setTaxInformationId] = useState();
  const [grauntorInformationId, setGrauntorInformationId] = useState();

  const [company, setCompany] = useState(initialCompanyState);
  //update company
  localStorage.setItem("CompanyId", companyId);
  // localStorage.setItem("addressIdForLocal", addressIdForLocal);
  // localStorage.setItem("ContactInformationId", contactInformationId);
  const [contactInfoIdForLocal, setContactInfoIdForLocal] = useState();
  const [accountInfoIdForLocal, setAccountInfoIdForLocal] = useState();
  const [tradeInfoIdForLocal, setTradeInfoIdForLocal] = useState();

  // const { Id } = useParams();AddressId

  //edit address end
  //update company information
  const [editCompany, setEditCompany] = useState({
    Id: companyId,
    Name: "",
    NoOfBranch: "",
    EstablishmentYear: "",
    TINNumber: "",
    TINCertificate: "",
    CompanyTypeID: "",
    VATNumber: "",
    VATCertificate: "",
    EntityTypeID: "",
  });
  const [editAddress, setEditAddress] = useState({
    Id: addressId,
    CountryId: "",
    RegionId: "",
    ZoneSubCity: "",
    Woreda: "",
    City: "",
    Kebele: "",
    HouseNo: "",
    BuildingNo: "",
    StreetAddress: "",
  });
  const [editContactInformation, setEditContactInformation] = useState({
    Id: contactInformationId,
    DesignatedPersonFName: "",
    DesignatedPersonMName: "",
    DesignatedPersonLName: "",
    Position: "",
    MobileNo: "",
    Telephone: "",
    Email: "",
  });
  const [editAccountInfo, setEditAccountInfo] = useState({
    Id: accountInformationId,
    AccountNo: "",
    CustomerId: "",
    Branch: "",
    District: "",
    Other: "",
  });
  const [editTradeInfo, setEditTradeInfo] = useState({
    Id: tradeInformationId,
    TradeRegistrationNo: "",
    UploadTradeRegCerti: selectedPathTRNEdit,
    TradeLicenseNo: "",
    IssueDate: "",
    RenewedDate: "",
    ValidityDate: "",
    TradeBusinessTypeID: "",
    UploadTradeLicense: selectedPathLinEdit,
  });
  const [editTaxInfo, setEditTaxInfo] = useState({
    Id: taxInformationId,
    Name: "",
    TinNo: "",
    IssueDate: "",
    Expireddate: "",
    TaxDebi: "",
    TaxClearanceCertificate: "",
  });

  const [editSoleAndPrtinershipInfo, setEditSoleAndPrtinershipInfo] = useState({
    Id: soleAndPartinershipInformationId,
    CompanyID: "",
    GuarantorId: "",
    TaxClearanceID: "",
    FirstName: "",
    MiddleName: "",
    LastName: "",
    NationalityID: "",
    CountryOfOriginID: "",
    CountryOfResidenceID: "",
    ProofOfIdentityTypeID: "",
    UploadProofOfIdentity: "",
    ProofOfIdentityIssuedDate: "",
    ProofOfIdentityExpiredDate: "",
    MartailStatusID: "",
  });
  //start
  const getSoleANdPartinershipById = (soleAndPartinershipInformationId) => {
    loanDataService
      .getSolleAndPartinershipById(soleAndPartinershipInformationId)
      .then((res) => {
        setEditSoleAndPrtinershipInfo({
          ...editSoleAndPrtinershipInfo,
          Id: res.data.Result.Id,
          CompanyID: res.data.Result.CompanyID,
          GuarantorId: res.data.Result.GuarantorId,
          TaxClearanceID: res.data.Result.TaxClearanceID,
          FirstName: res.data.Result.FirstName,
          MiddleName: res.data.Result.MiddleName,
          LastName: res.data.Result.LastName,
          NationalityID: res.data.Result.NationalityID,
          CountryOfOriginID: res.data.Result.CountryOfOriginID,
          CountryOfResidenceID: res.data.Result.CountryOfResidenceID,
          ProofOfIdentityTypeID: res.data.Result.ProofOfIdentityTypeID,
          UploadProofOfIdentity: res.data.Result.UploadProofOfIdentity,
          ProofOfIdentityIssuedDate: res.data.Result.ProofOfIdentityIssuedDate,
          ProofOfIdentityExpiredDate:
            res.data.Result.ProofOfIdentityExpiredDate,
          MartailStatusID: res.data.Result.MartailStatusID,
        });
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    getSoleANdPartinershipById();
  }, []);
  const updateSoleAndPartinershipInfo = () => {
    loanDataService
      .getSolleAndPartinership(
        soleAndPartinershipInformationId,
        editSoleAndPrtinershipInfo
      )
      .then((response) => {
        // navigation("/company/1");
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    updateSoleAndPartinershipInfo();
  }, []);

  //end
  const updateTaxById = (taxInformationId) => {
    loanDataService
      .getSoleTax1(taxInformationId)
      .then((res) => {
        setEditTaxInfo({
          ...editTaxInfo,
          Id: taxInformationId,
          Name: res.data.Result.Name,
          TinNo: res.data.Result.TinNo,
          IssueDate: res.data.Result.IssueDate,
          Expireddate: res.data.Result.Expireddate,
          TaxDebi: res.data.Result.TaxDebi,
          TaxClearanceCertificate: res.data.Result.TaxClearanceCertificate,
        });
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    updateTaxById();
  }, []);
  const updateTaxInfo = () => {
    loanDataService
      .updateTax(taxInformationId, editTaxInfo)
      .then((response) => {
        // navigation("/company/1");
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    updateTaxInfo();
  }, []);

  const updateCompanyById = (companyId) => {
    loanDataService
      .getCompanyById(companyId)
      .then((res) => {
