                          />
                          <TextField
                            id="MinuteReferenceNumber"
                            label="MinuteReferenceNumber"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={editPLCAndShareInfo.MinuteReferenceNumber}
                            onChange={(e) =>
                              setEditPLCAndShareInfo({
                                ...editPLCAndShareInfo,
                                MinuteReferenceNumber: e.target.value,
                              })
                            }
                            name="MinuteReferenceNumber"
                          />
                          <TextField
                            id="MinuteAuthDate"
                            label="MinuteAuthDate"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={editPLCAndShareInfo.MinuteAuthDate}
                            onChange={(e) =>
                              setEditPLCAndShareInfo({
                                ...editPLCAndShareInfo,
                                MinuteAuthDate: e.target.value,
                              })
                            }
                            name="MinuteAuthDate"
                          />
                          <TextField
                            id="MinuteAuthRefNumber"
                            label="MinuteAuthRefNumber"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={editPLCAndShareInfo.MinuteAuthRefNumber}
                            onChange={(e) =>
                              setEditPLCAndShareInfo({
                                ...editPLCAndShareInfo,
                                MinuteAuthRefNumber: e.target.value,
                              })
                            }
                            name="MinuteAuthRefNumber"
                          />
                          <div>
                            {currentFile && (
                              <div className="progress">
                                <div
                                  className="progress-bar progress-bar-info progress-bar-striped"
                                  role="progressbar"
                                  aria-valuenow={progress}
                                  aria-valuemin="0"
                                  aria-valuemax="100"
                                  style={{ width: progress + "%" }}
                                >
                                  {progress}%
                                </div>
                              </div>
                            )}
                            <label className="btn btn-default">
                              <input type="file" onChange={selectFile6Edit} />
                            </label>

                            <button
                              className="btn btn-success"
                              disabled={!selectedFiles}
                              onClick={upload6Edit}
                            >
                              Upload
                            </button>
                            <div className="alert alert-light" role="alert">
                              {message}
                            </div>
                          </div>
                          <TextField
                            id="MinuteIssuer"
                            label="MinuteIssuer"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={editPLCAndShareInfo.MinuteIssuer}
                            onChange={(e) =>
                              setEditPLCAndShareInfo({
                                ...editPLCAndShareInfo,
                                MinuteIssuer: e.target.value,
                              })
                            }
                            name="MinuteIssuer"
                          />
                          <TextField
                            id="MinuteAgenda"
                            label="MinuteAgenda"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={editPLCAndShareInfo.MinuteAgenda}
                            onChange={(e) =>
                              setEditPLCAndShareInfo({
                                ...editPLCAndShareInfo,
                                MinuteAgenda: e.target.value,
                              })
                            }
                            name="MinuteAgenda"
                          />
                        </div>
                        <button type="submit" className="btn btn-info">
                          Update
                        </button>
                      </div>
                    </div>
                  </form>
                </div>
              </CompanyModal>
              <div>
                <CompanyModal
                  isOpen={isCompanyTaxSoleModalOpen}
                  onClose={handleCompanyTaxSoleCloseModal}
                  title="Add company PLC tax information"
                >
                  <div className="rounded-lg overflow-hidden">
                    {getSoleTax && (
                      <table className="table table-bordered table-striped bg-purple-100">
                        <thead>
                          <th className="px-2 py-4"> Name </th>
                          <th className="px-2 py-4"> TinNo </th>
                          <th className="px-2 py-4"> Issue Date </th>
                          <th className="px-2 py-4"> Expired date </th>
                          <th className="px-2 py-4"> Tax Debi </th>
                          <th className="px-2 py-4">
                            Tax Clearance Certificate
                          </th>
                          <th className="px-2 py-4"> Action </th>
                        </thead>
                        <tbody>
                          {/* {getSoleTax.map((soleTax, index) => ( */}
                          <tr
                          // key={index}
                          >
                            {/* <td>{getSoleTax.Id}</td> */}
                            <td>{getSoleTax.Name}</td>
                            <td>{getSoleTax.TinNo}</td>
                            <td>{getSoleTax.IssueDate}</td>
                            <td>{getSoleTax.Expireddate}</td>
                            <td>{getSoleTax.TaxDebi}</td>
                            <td>{getSoleTax.TaxClearanceCertificate}</td>
                            <button
                              onClick={() => {
                                handleCompanyTaxInformationEditOpenModal();
                                updateTaxById(getSoleTax.Id);
                                // saveTaxSoleInfo(sole.Id);
                              }}
                              className="text-purple-600 rounded-md font-semibold "
                            >
                              <span>
                                <IconButton aria-label="Add" color="secondary">
                                  <BsPlus /> Edit
                                </IconButton>
                              </span>
                            </button>
                          </tr>
                          {/* ))}  */}
                        </tbody>
                      </table>
                    )}
                  </div>
                  <button
                    onClick={() => setToggleTaxInfo(!toggleTaxSoleInfo)}
                    className="text-purple-600 rounded-md font-medium "
                  >
                    Add Tax
                  </button>
                  {toggleTaxSoleInfo && (
                    <div className="submit-form">
                      {submitted ? (
                        <div>
                          <h4>You submitted successfully!</h4>
                          <button
                            className="btn btn-success"
                            onClick={newTaxSoleInfo}
                          >
                            Add
                          </button>
                        </div>
                      ) : (
                        <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                          <TextField
                            id="Name"
                            label="Name"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.Name}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="Name"
                          />
                          <TextField
                            id="TinNo"
                            label="TinNo"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.TinNo}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="TinNo"
                          />
                          <TextField
                            id="IssueDate"
                            label="IssueDate"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.IssueDate}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="IssueDate"
                          />
                          <TextField
                            id="ExpiredDate"
                            label="ExpiredDate"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.ExpiredDate}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="ExpiredDate"
                          />
                          <TextField
                            id="TaxDebi"
                            label="TaxDebi"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.TaxDebi}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="TaxDebi"
                          />
                          <TextField
                            id="TaxClearanceCertificate"
                            label="TaxClearanceCertificate"
                            variant="standard"
                            color="secondary"
                            size=""
                            required
                            value={taxSoleInfo.TaxClearanceCertificate}
                            onChange={handleInputChangeTaxSoleInfo}
                            name="TaxClearanceCertificate"
                          />
                        </div>
                      )}
                      <button
                        className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                        onClick={() => {
                          saveTaxSoleInfo();
                          setToggleTaxInfo(!toggleTaxSoleInfo);
                        }}
                      >
                        save Tax
                      </button>
                    </div>
                  )}
                </CompanyModal>
              </div>
            </>
          )}
          {activeStep === 9 && (
            <div>
              <h2 className="text-2xl font-bold mb-4">
                Thank you for submitting the form!
              </h2>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
export default LeftNav;
