import { useState, useEffect } from "react";
import CompanyModal from "../../Common/CompanyModal";
import LoanTextField from "../../../Common/LoanTextField";
import { Select, FormControl, MenuItem, InputLabel } from "@mui/material";
import CommonServices from "../../Common/CommonServices";
import ViewMarriedTax from "./ViewMarriedTax";
import AddMaritalStatus from "./AddMaritalStatus";
const ViewMaritalStatus = ({
  holdMarried,
  SoleId,
  isCompanyMartailStatusModalOpen,
  handleCompanyMartailStatusCloseModal,
  handleMaritalStatusModalOpen,
}) => {
  //upload start
const [selectedPathIdentity, setSelectedPathIdentity] = useState(null);
const [selectedPathMarriege, setSelectedPathMarriege] = useState(null);
const [selectedFiles, setSelectedFiles] = useState(undefined);
const [currentFile, setCurrentFile] = useState(undefined);
const [fileInfos, setFileInfos] = useState([]);
const [progress, setProgress] = useState(0);
const [message, setMessage] = useState("");
const selectFile = (event) => {
  setSelectedFiles(event.target.files);
};
//upload 1
const uploadIdentity = () => {
  let currentFile = selectedFiles[0];
  setProgress(0);
  setCurrentFile(currentFile);
  CommonServices.upload(currentFile, (event) => {
    setProgress(Math.round((100 * event.loaded) / event.total));
  })
    .then((response) => {
      setMessage(response.data.message);
      setSelectedPathIdentity(response.data.Result);
    })
    .then((files) => {
      setFileInfos(files.data);
    })
    .catch(() => {
      setProgress(0);
      setCurrentFile(undefined);
    });
  setSelectedFiles(undefined);
};
//end upload
//upload 2
const uploadMarriegeCertificate = () => {
  let currentFile = selectedFiles[0];
  setProgress(0);
  setCurrentFile(currentFile);
  CommonServices.upload(currentFile, (event) => {
    setProgress(Math.round((100 * event.loaded) / event.total));
  })
    .then((response) => {
      setMessage(response.data.message);
      setSelectedPathMarriege(response.data.Result);
    })
    .then((files) => {
      setFileInfos(files.data);
    })
    .catch(() => {
      setProgress(0);
      setCurrentFile(undefined);
    });
  setSelectedFiles(undefined);
};
//endupload2
  //uppload end
  //date
  const [marriageDate, setMarriageDate] = useState(new Date());
  const [proofOfIdentityIssueDate, setProofOfIdentityIssueDate] = useState(new Date());
  const [proofOfIdentityExpirDate, setProofOfIdentityExpirDate] = useState(new Date());
  //end date
  const [holdMarriedTax, setMarriedTax] = useState([]);
  const [country, setContry] = useState([]);
  const [proofOfIdentityType, setProofOfIdentityType] = useState([]);
  const [
    isCompanyMarriedInformationEditModalOpen,
    setIsCompanyMarriedInformationEditModalOpen,
  ] = useState(false);
  function handleCompanyMarriedEditCloseModal() {
    setIsCompanyMarriedInformationEditModalOpen(false);
  }
  function handleCompanyMarriedInformationEditOpenModal() {
    setIsCompanyMarriedInformationEditModalOpen(true);
  }
  function handleCompanyMarriedInformationEditCloseModal() {
    setIsCompanyMarriedInformationEditModalOpen(false);
  }
  //married tax
  const [isCompanyMarriedTaxModalOpen, setIsCompanyMarriedTaxModalOpen] =
    useState(false);
  function handleCompanyMarriedTaxOpenModal() {
    setIsCompanyMarriedTaxModalOpen(true);
  }
  function handleCompanyMarriedTaxCloseModal() {
    setIsCompanyMarriedTaxModalOpen(false);
  }
  //end married tax
  const [taxInformationId, setTaxInformationId] = useState();
  const [marriedTaxId, setMarriedTaxId] = useState();
  const [editMarriedInfo, setEditMarriedInfo] = useState({
    Id: taxInformationId,
    FirstName: "",
    MiddleName: "",
    LastName: "",
    NationalityID: "",
    CountryOfOriginID: "",
    CountryOfResidenceID: "",
    ProofOfIdentityTypeID: "",
    ProofOfIdentity: "",
    MarriageDate: "",
    MarriageCertificate: selectedPathMarriege,
    MarriageCertificateIssuer: "",
    ProofOfIdentityIssueDate: "",
    ProofOfIdentityExpirDate: "",
  });
  const handleInputChangeEditMarriedStatus = (event) => {
    const { name, value } = event.target;
    setEditMarriedInfo({ ...editMarriedInfo, [name]: value });
  };
  const updateMarriedById = (taxInformationId) => {
    CommonServices.getMarried(taxInformationId)
      .then((res) => {
        setEditMarriedInfo({ 
          ...editMarriedInfo,
          Id: taxInformationId,
          FirstName: res.data.Result.FirstName,
          MiddleName: res.data.Result.MiddleName,
          LastName: res.data.Result.LastName,
          NationalityID: res.data.Result.NationalityID,
          CountryOfOriginID: res.data.Result.CountryOfOriginID,
          CountryOfResidenceID: res.data.Result.CountryOfResidenceID,
          ProofOfIdentityTypeID: res.data.Result.ProofOfIdentityTypeID,
          ProofOfIdentity: res.data.Result.ProofOfIdentity,
          MarriageDate: res.data.Result.MarriageDate,
          MarriageCertificate: res.data.Result.MarriageCertificate,
          MarriageCertificateIssuer: res.data.Result.MarriageCertificateIssuer,
          ProofOfIdentityIssueDate: res.data.Result.ProofOfIdentityIssueDate,
          ProofOfIdentityExpirDate: res.data.Result.ProofOfIdentityExpirDate,
        });
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    updateMarriedById();
  }, []);
  const updateMarriedInfo = () => {
    CommonServices.updateMarried(taxInformationId, editMarriedInfo)
      .then((response) => {})
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    updateMarriedInfo();
  }, []);
  const fetchContry = () => {
    CommonServices.getCountry()
      .then((response) => {
        setContry(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchContry();
  }, []);

  const fetchProofOfIdentityType = () => {
    CommonServices.getProofOfIdentityTypes()
      .then((response) => {
        setProofOfIdentityType(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchProofOfIdentityType();
  }, []);
  const getMarriedTax = (marriedId) => {
    CommonServices.getTaxByMarriedId(marriedId)
      .then((response) => {
        setMarriedTax(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  return (
    <div>
      <CompanyModal
        isOpen={isCompanyMartailStatusModalOpen}
        onClose={handleCompanyMartailStatusCloseModal}
        title="Add marital status information"
      >
        <div className="rounded-lg overflow-hidden overflow-x-scroll overflow-y-scroll">
        <ViewMarriedTax
                holdMarriedTax={holdMarriedTax}
                isCompanyMarriedTaxModalOpen={
                  isCompanyMarriedTaxModalOpen
                }
                handleCompanyMarriedTaxCloseModal={
                  handleCompanyMarriedTaxCloseModal
                }
                handleCompanyMarriedTaxOpenModal={
                  handleCompanyMarriedTaxOpenModal
                }
                marriedTaxId = {marriedTaxId}
              />
          {holdMarried ? (
            <table className="table table-bordered table-striped bg-purple-100">
              <thead>
                <th className="px-2 py-4"> Name</th>
                <th className="px-2 py-4"> Nationality</th>
                <th className="px-2 py-4"> Country Of Origin</th>
                <th className="px-2 py-4"> Country Of Residence</th>
                <th className="px-2 py-4"> Proof Of Identity Type</th>
                <th className="px-2 py-4"> Proof Of Identity</th>
                <th className="px-2 py-4"> Marriage Date</th>
                <th className="px-2 py-4"> Marriage Certificate Issuer</th>
                <th className="px-2 py-4"> Proof Of Identity Issue Date</th>
                <th className="px-2 py-4"> Proof Of Identity Expir Date</th>
                <th className="px-2 py-4"> Action </th>
              </thead>
              <tbody>
                <tr>
                  <td>
                    {holdMarried.FirstName} {holdMarried.MiddleName}{" "}
                    {holdMarried.LastName}
                  </td>
                  <td>{holdMarried.Nationality}</td>
                  <td>{holdMarried.CountryOfOrigin}</td>
                  <td>{holdMarried.CountryOfResidence}</td>
                  <td>{holdMarried.ProofOfIdentityType}</td>
                  <td>{holdMarried.ProofOfIdentity}</td>
                  <td>{holdMarried.MarriageDate}</td>
                  <td>{holdMarried.MarriageCertificateIssuer}</td>
                  <td>{holdMarried.ProofOfIdentityIssueDate}</td>
                  <td>{holdMarried.ProofOfIdentityExpirDate}</td>
                  <td>{holdMarried.TaxClearanceID}</td>
                  <td>
                    <button
                      onClick={() => {
                        handleCompanyMarriedInformationEditOpenModal();
                        updateMarriedById(holdMarried.Id);
                        setTaxInformationId(holdMarried.Id);
                        setMarriedTaxId(holdMarried.Id)
                      }}
                      className="text-purple-600 rounded-md font-semibold "
                    >
                      Edit
                    </button>
                  </td>
                  <td>{"   "}</td>
                  <td>
                    <button
                      type="submit"
                      onClick={() => {
                        handleCompanyMarriedTaxOpenModal();
                        getMarriedTax(holdMarried.Id)
                        setMarriedTaxId(holdMarried.Id)
                      }}
                      className="text-purple-600 rounded-md font-semibold "
                    >
                      Tax
                    </button>
                  </td>                  
                </tr>
              </tbody>
            </table>
          ) : (
            ""
          )}
          <AddMaritalStatus  
          SoleId = {SoleId}
          />
        </div>        
      </CompanyModal>
      <CompanyModal
        isOpen={isCompanyMarriedInformationEditModalOpen}
        onClose={handleCompanyMarriedEditCloseModal}
        title="Married edit information"
      >
        <div>
          <form onSubmit={updateMarriedInfo}>
            <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              <LoanTextField
                id="FirstName"
                label="FirstName"
                value={editMarriedInfo.FirstName}
                onChange={handleInputChangeEditMarriedStatus}
                name="FirstName"
              />
              <LoanTextField
                id="MiddleName"
                label="MiddleName"
                value={editMarriedInfo.MiddleName}
                onChange={handleInputChangeEditMarriedStatus}
                name="MiddleName"
              />
              <LoanTextField
                id="LastName"
                label="LastName"
                value={editMarriedInfo.LastName}
                onChange={handleInputChangeEditMarriedStatus}
                name="LastName"
              />
              <FormControl variant="standard" sx={{ minWidth: 120 }}>
                <InputLabel id="countrylbl" color="secondary">
                  {" "}
                  Nationality{" "}
                </InputLabel>
                <Select
                  color="secondary"
                  onChange={(event) =>
                    (editMarriedInfo.NationalityID = event.target.value)
                  }
                  labelId="countrylbl"
                  id="countrySelect"
                  label="Country"
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  {country.map((yourCounty) => (
                    <MenuItem
                      key={yourCounty.Id}
                      NationalityID={yourCounty.Id}
                      value={yourCounty.Id}
                    >
                      {yourCounty.Name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
              <FormControl variant="standard" sx={{ minWidth: 120 }}>
                <InputLabel id="countrylbl" color="secondary">
                  {" "}
                  Country Of Origin{" "}
                </InputLabel>
                <Select
                  color="secondary"
                  onChange={(event) =>
                    (editMarriedInfo.CountryOfOriginID = event.target.value)
                  }
                  labelId="countrylbl"
                  id="countrySelect"
                  label="Country"
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  {country.map((yourCounty) => (
                    <MenuItem
                      key={yourCounty.Id}
                      NationalityID={yourCounty.Id}
                      value={yourCounty.Id}
                    >
                      {yourCounty.Name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
              <FormControl variant="standard" sx={{ minWidth: 120 }}>
                <InputLabel id="countrylbl" color="secondary">
                  {" "}
                  Country Of Residence{" "}
                </InputLabel>
                <Select
                  color="secondary"
                  onChange={(event) =>
                    (editMarriedInfo.CountryOfResidenceID = event.target.value)
                  }
                  labelId="countrylbl"
                  id="countrySelect"
                  label="Country"
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  {country.map((yourCounty) => (
                    <MenuItem
                      key={yourCounty.Id}
                      NationalityID={yourCounty.Id}
                      value={yourCounty.Id}
                    >
                      {yourCounty.Name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
              <FormControl variant="standard" sx={{ minWidth: 120 }}>
                <InputLabel id="countrylbl" color="secondary">
                  {" "}
                  Proof Of Identity Type{" "}
                </InputLabel>
                <Select
                  color="secondary"
                  onChange={(event) =>
                    (editMarriedInfo.ProofOfIdentityTypeID = event.target.value)
                  }
                  labelId="countrylbl"
                  id="countrySelect"
                  label="Country"
                >
                  <MenuItem value="">
                    <em>None</em>
                  </MenuItem>
                  {proofOfIdentityType.map((yourProofOfIdentityType) => (
                    <MenuItem
                      key={yourProofOfIdentityType.Id}
                      ProofOfIdentityTypeID={yourProofOfIdentityType.Id}
                      value={yourProofOfIdentityType.Id}
                    >
                      {yourProofOfIdentityType.Name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
              {/* <LoanTextField
                id="ProofOfIdentity"
                label="ProofOfIdentity"
                value={editMarriedInfo.ProofOfIdentity}
                onChange={handleInputChangeEditMarriedStatus}
                name="ProofOfIdentity"
              /> */}
               <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
               className="bg-[#c94fc9] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  disabled={!selectedFiles}
                  // onClick=()=>{
                  //   uploadIdentity();
                  //   editMarriedInfo.ProofOfIdentity ={}
                  
                  // }
                  onClick={() => {
                    uploadIdentity();
                    editMarriedInfo.ProofOfIdentity = selectedPathIdentity
                  }}

                  
                >
                  Upload Proof Of Identity
                </button>
                <div className="alert alert-light" role="alert">
                  {message}                  
                </div>
              </div>
               <p>
               Marriage Date:
                <input
                  id="MarriageDate"
                  name="MarriageDate"
                  label="MarriageDate"
                  placeholder="MarriageDate"
                  type="date"
                  value={
                    marriageDate.getFullYear().toString() +
                    "-" +
                    (marriageDate.getMonth() + 1)
                      .toString()
                      .padStart(2, 0) +
                    "-" +
                    marriageDate
                      .getDate()
                      .toString()
                      .padStart(2, 0)
                  }
                  onChange={(e) => {
                    setMarriageDate(new Date(e.target.value));
                    editMarriedInfo.MarriageDate =
                    marriageDate;
                  }}
                />{" "}
              </p>
              <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
              className="bg-[#c94fc9] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"

                  disabled={!selectedFiles}
                  onClick={uploadMarriegeCertificate}
                >
                  Upload Marriage Certificate
                </button>
                <div className="alert alert-light" role="alert">
                  {message}
                </div>
              </div>
              {/* <LoanTextField
                id="MarriageCertificate"
                label="MarriageCertificate"
                value={editMarriedInfo.MarriageCertificate}
                onChange={handleInputChangeEditMarriedStatus}
                name="MarriageCertificate"
              /> */}
              <LoanTextField
                id="MarriageCertificateIssuer"
                label="MarriageCertificateIssuer"
                value={editMarriedInfo.MarriageCertificateIssuer}
                onChange={handleInputChangeEditMarriedStatus}
                name="MarriageCertificateIssuer"
              />
 <p>
               Proof Of Identity Issue Date:
                <input
                  id="ProofOfIdentityIssueDate"
                  name="ProofOfIdentityIssueDate"
                  label="ProofOfIdentityIssueDate"
                  placeholder="ProofOfIdentityIssueDate"
                  type="date"
                  value={
                    proofOfIdentityIssueDate.getFullYear().toString() +
                    "-" +
                    (proofOfIdentityIssueDate.getMonth() + 1)
                      .toString()
                      .padStart(2, 0) +
                    "-" +
                    proofOfIdentityIssueDate
                      .getDate()
                      .toString()
                      .padStart(2, 0)
                  }
                  onChange={(e) => {
                    setProofOfIdentityIssueDate(new Date(e.target.value));
                    editMarriedInfo.ProofOfIdentityIssueDate =
                    proofOfIdentityIssueDate;
                  }}
                />{" "}
              </p>
               <p>
               Proof Of Identity Expir Date:
                <input
                  id="ProofOfIdentityExpirDate"
                  name="ProofOfIdentityExpirDate"
                  label="ProofOfIdentityExpirDate"
                  placeholder="ProofOfIdentityExpirDate"
                  type="date"
                  value={
                    proofOfIdentityExpirDate.getFullYear().toString() +
                    "-" +
                    (proofOfIdentityExpirDate.getMonth() + 1)
                      .toString()
                      .padStart(2, 0) +
                    "-" +
                    proofOfIdentityExpirDate
                      .getDate()
                      .toString()
                      .padStart(2, 0)
                  }
                  onChange={(e) => {
                    setProofOfIdentityIssueDate(new Date(e.target.value));
                    editMarriedInfo.ProofOfIdentityExpirDate =
                    proofOfIdentityExpirDate;
                  }}
                />{" "}
              </p>
            </div>
            <div>
              <button className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                Update
              </button>
            </div>
          </form>
        </div>
      </CompanyModal>
    </div>
  );
};
export default ViewMaritalStatus;
