import {
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  IconButton,
} from "@mui/material";
import LoanIconButton from "../../Common/LoanIconButton";
import CompanyModal from "../Common/CompanyModal";
import { RiSkipBackMiniFill } from "react-icons/ri";
import { BsPlus } from "react-icons/bs";
import { BiSkipNext, BiSkipPrevious } from "react-icons/bi";
import { useState } from "react";
import { IoRefresh } from "react-icons/io5";
import { Link } from "react-router-dom";
import LoanTextField from "../../Common/LoanTextField";
import { useParams } from "react-router-dom";
const AddAccountDetail = ({
  accountDetial,
  handleInputChangeAccountDetial,
  handleBack,
  handleSkip,
  handleNext,
  saveAccountDetials,
  handlePrevios,
}) => {
  const [isAccountDetailModalOpen, setIsAccountDetailModalOpen] =
    useState(false);
  function handleAccountDetailOpenModal() {
    setIsAccountDetailModalOpen(true);
  }
  function handleAccountDetailCloseModal() {
    setIsAccountDetailModalOpen(false);
  }
  const { companyId } = useParams();
  return (
    <>
      <div className="flex">
        <Link to={`/Account/${companyId}`}>
          <LoanIconButton
            style={{ color: "purple" }}
            icon={<BiSkipPrevious />}
            text="Account Information"
            onClick={handlePrevios}
          />
        </Link>
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BsPlus />}
          text="Add"
          onClick={handleAccountDetailOpenModal}
        />
        <Link to={`/trade/${companyId}`}>
          <LoanIconButton
            style={{ color: "purple" }}
            icon={<BiSkipNext />}
            text="Trade Registration"
            onClick={handleSkip}
          />
        </Link>
      </div>
      <CompanyModal
        isOpen={isAccountDetailModalOpen}
        onClose={handleAccountDetailCloseModal}
        title="Add account detail information"
      >
        <div>
          <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            <LoanTextField
              id="CustomerId"
              label="Customer Id"
              value={accountDetial.CustomerId}
              onChange={handleInputChangeAccountDetial}
              name="CustomerId"
            />
            <LoanTextField
              id="DepositAccount"
              label="DepositAccount"
              value={accountDetial.DepositAccount}
              onChange={handleInputChangeAccountDetial}
              name="DepositAccount"
            />
            <LoanTextField
              id="DepositBalance"
              label="DepositBalance"
              value={accountDetial.DepositBalance}
              onChange={handleInputChangeAccountDetial}
              name="DepositBalance"
            />
          </div>
          <div className="flex justify-center p-2 pt-5">
            <button
              className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              onClick={() => {
                saveAccountDetials();
              }}
            >
              Save
            </button>
          </div>
        </div>
      </CompanyModal>
    </>
  );
};
export default AddAccountDetail;
