import { useState, useEffect } from "react";
import { FaSpinner } from "react-icons/fa";
import { BiSkipNext } from "react-icons/bi";
import { MdModeEdit } from "react-icons/md";
import { IoTrashOutline } from "react-icons/io5";
import { useParams, useNavigate } from "react-router-dom";
import CompanyNavigation from "../../Common/CompanyNavigation";
import LoanIconButton from "../../Common/LoanIconButton";
import AddCompanyAddress from "./AddCompanyAddress";
import { ToastContainer, toast } from "react-toastify";
import { Link } from "react-router-dom";
import CompanyAddressService from "./CompanyAddressService";
import CommonServices from "../Common/CommonServices";
import EditCompanyAddress from "./EditCompanyAddress";
import loanDataService from "../Service";
const ViewCompanyAddress = () => {
  const navigate = useNavigate();
  const { companyId } = useParams();
  const [addresses, setAddresses] = useState([]);
  const [error, setError] = useState(null);
  const [addressErrorLoading, setAddressErrorLoading] = useState(false);

  const [isCompanyAddressEditModalOpen, setIsCompanyAddressEditModalOpen] =
    useState(false);

  function handleCompanyAddressEditOpenModal() {
    setIsCompanyAddressEditModalOpen(true);
  }
  function handleCompanyAddressEditCloseModal() {
    setIsCompanyAddressEditModalOpen(false);
  }

  const [isCompanyModalOpen, setIsCompanyModalOpen] = useState(false);

  function handleGeneralCloseModal() {
    setIsCompanyModalOpen(false);
  }
  function handleGeneralOpenModal() {
    setIsCompanyModalOpen(true);
  }
  const handleDelete = () => {};
  const getCompanyAddresses = () => {
    setAddressErrorLoading(true);
    setTimeout(() => {
      CompanyAddressService.getCompanyAddress(companyId)
        .then((response) => {
          if (
            response.data.Code === 200 ||
            response.data.Message === "Success"
          ) {
            setAddresses(response.data.Result);
            setAddressErrorLoading(false);
          } else {
            setAddressErrorLoading(false);
            setError("Something went wrong");
          }
        })
        .catch((error) => {
          if (error.response.data.StatusCode === 404) {
            setError("Address not found");
            setAddressErrorLoading(false);
          } else if (error.response.data.StatusCode === 400) {
            setError("Something went wrong");
            setAddressErrorLoading(false);
          } else if (error.response.data.StatusCode === 401) {
            navigate("/login");
          } else if (error.response.data.StatusCode === 403) {
            navigate("/login");
          }
        });
    }, 1000);
  };
  useEffect(() => {
    getCompanyAddresses();
  }, []);
  const [country, setCountries] = useState([]);
  const [region, setRegion] = useState([]);
  const fetchContry = () => {
    CommonServices.getCountry()
      .then((response) => {
        setCountries(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchContry();
  }, []);
  const fetchRegion = () => {
    CommonServices.getRegion()
      .then((response) => {
        setRegion(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    fetchRegion();
  }, []);
  const handleInputChangeAdress = (event) => {
    const { name, value } = event.target;
    setAddress({ ...address, [name]: value });
  };

  const initialAdressState = {
    Id: null,
    CountryID: "",
    RegionID: "",
    ZoneSubCity: "",
    Woreda: "",
    City: "",
    Kebele: "",
    HouseNo: "",
    BuildingNo: "",
    StreetAddress: "",
  };
  const [address, setAddress] = useState(initialAdressState);
  const newAddress = () => {
    setAddress(initialAdressState);
    // setSubmitted(false);
  };
  const saveAddress = () => {
    var data = {
      CountryID: address.CountryID,
      RegionID: address.RegionID,
      ZoneSubCity: address.ZoneSubCity,
      Woreda: address.Woreda,
      City: address.City,
      Kebele: address.Kebele,
      HouseNo: address.HouseNo,
      BuildingNo: address.BuildingNo,
      StreetAddress: address.StreetAddress,
    };
    CompanyAddressService.addAdress(data, companyId)
      .then((response) => {
        setAddress({
          Id: response.data.Id,
          CountryID: response.data.CountryID,
          RegionID: response.data.RegionID,
          ZoneSubCity: response.data.ZoneSubCity,
          Woreda: response.data.Woreda,
          City: response.data.City,
          Kebele: response.data.Kebele,
          HouseNo: response.data.HouseNo,
          BuildingNo: response.data.BuildingNo,
          StreetAddress: response.data.StreetAddress,
        });
        // setSubmitted(true);
        console.log(response.data);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  const [companyAddressId, setCompanyAddressId] = useState();

  const [editAddress, setEditAddress] = useState({
    Id: companyAddressId,
    CountryId: "",
    RegionId: "",
    ZoneSubCity: "",
    Woreda: "",
    City: "",
    Kebele: "",
    HouseNo: "",
    BuildingNo: "",
    StreetAddress: "",
  });
  const getAddressByCompanyId = (addressId) => {
    CompanyAddressService.getAddressById(addressId)
      .then((res) => {
        setEditAddress({
          ...editAddress,
          Id: addressId,
          CountryId: res.data.Result.CountryId,
          RegionId: res.data.Result.RegionId,
          ZoneSubCity: res.data.Result.ZoneSubCity,
          Woreda: res.data.Result.Woreda,
          City: res.data.Result.City,
          Kebele: res.data.Result.Kebele,
          HouseNo: res.data.Result.HouseNo,
          BuildingNo: res.data.Result.BuildingNo,
          StreetAddress: res.data.Result.StreetAddress,
        });
        debugger;
      })
      .catch((err) => {
        if (err.response.data.StatusCode === 404) {
          console.log(err.response.data.Message);
        }
      });
  };
  const updateCompanyAddressById = (e) => {
    e.preventDefault();
    CompanyAddressService.updateCompanyAddressById(
      companyAddressId,
      editAddress
    )
      .then((response) => {
        if (response.data.Code === 200) {
          getCompanyAddresses();
          toast.success("Added successfully", {
            position: toast.POSITION.TOP_RIGHT,
            toastId: "updateCompanyAddressSuccess",
          });
        }
      })
      .catch((e) => {
        toast.error("Something went wrong", {
          position: toast.POSITION.TOP_RIGHT,
          toastId: "updateCompanyAddressError",
        });
      });
  };
  //   useEffect(() => {
  //     updateCompanyAddressById();
  //   }, []);
  const handleBack = () => {
    alert("Handle Back");
  };
  const handleSkip = () => {
    alert("Handle Back");
  };
  const handleNext = () => {
    alert("Handle Next");
  };
  return (
    <div className="flex">
      <CompanyNavigation />
      <div className="px-4 py-3">
        <div className="flex flex-wrap">
          <div className="w-full">
            <div>
              <h4 className="text-lg font-semibold eading-tight p-2">
                Company Address
              </h4>
              <AddCompanyAddress
                // submitted={submitted}
                newAddress={newAddress}
                address={address}
                country={country}
                region={region}
                handleInputChangeAdress={handleInputChangeAdress}
                handleBack={handleBack}
                handleSkip={handleSkip}
                handleNext={handleNext}
                saveAddress={saveAddress}
              />
              <EditCompanyAddress
                isCompanyAddressEditModalOpen={isCompanyAddressEditModalOpen}
                handleCompanyAddressEditCloseModal={
                  handleCompanyAddressEditCloseModal
                }
                updateCompanyAddressById={updateCompanyAddressById}
                editAddress={editAddress}
                country={country}
                region={region}
                setEditAddress={setEditAddress}
                // setEditCompany={setEditCompany}
              />
              <ToastContainer />
              {error && <p className="text-red-500">{error}</p>}
              {!error && addressErrorLoading ? (
                <span>
                  <FaSpinner
                    className="animate-spin rounded-full h-6 w-6 text-purple-600"
                    fontSize="small"
                    data-toggle="tooltip"
                    title="Click it to sign in"
                  />
                  Loading...
                </span>
              ) : (
                !error &&
                !addressErrorLoading && (
                  <table className="table table-bordered table-striped bg-purple-100">
                    <thead>
                      <tr className="bg-purple-200 ">
                        <th className="px-2 py-4"> Country </th>
                        <th className="px-2 py-4"> RegionID </th>
                        <th className="px-2 py-4"> ZoneSubCity </th>
                        <th className="px-2 py-4"> Woreda </th>
                        <th className="px-2 py-4"> City </th>
                        <th className="px-2 py-4"> Kebele </th>
                        <th className="px-2 py-4"> HouseNo </th>
                        <th className="px-2 py-4">BuildingNo</th>
                        <th className="px-2 py-4">StreetAddress</th>
                        <th className="px-2 py-4"> Action </th>
                      </tr>
                    </thead>
                    <tbody>
                      {addresses && (
                        <>
                          <tr
                            className="bg-white
                    border-b border-purple-200"
                            key={addresses.Id}
                          >
                            <td className="p-2">{addresses.Country}</td>
                            <td className="p-2">{addresses.Region}</td>
                            <td className="p-2">{addresses.ZoneSubCity}</td>
                            <td className="p-2">{addresses.Woreda}</td>
                            <td className="p-2">{addresses.City}</td>
                            <td className="p-2">{addresses.Kebele}</td>
                            <td className="p-2">{addresses.HouseNo}</td>
                            <td className="p-2">{addresses.BuildingNo}</td>
                            <td className="p-2">{addresses.StreetAddress}</td>
                            <td className="p-2">
                              <LoanIconButton
                                style={{ color: "green" }}
                                icon={<MdModeEdit />}
                                text="Edit"
                                onClick={() => {
                                  handleCompanyAddressEditOpenModal();
                                  setCompanyAddressId(addresses.Id);
                                  //   updateCompanyById(addresses.Id);
                                  getAddressByCompanyId(addresses.Id);
                                }}
                              />
                              <>
                                <LoanIconButton
                                  style={{ color: "red" }}
                                  icon={<IoTrashOutline />}
                                  text="Delete"
                                  onClick={() => {
                                    handleDelete(addresses);
                                  }}
                                >
                                  <ToastContainer />
                                </LoanIconButton>
                              </>
                              <Link
                                to={`/enterprise/fetch-account-details/${companyId}`}
                              >
                                <LoanIconButton
                                  style={{ color: "purple" }}
                                  icon={<BiSkipNext />}
                                  text="Account Detial"
                                />
                              </Link>
                            </td>
                          </tr>
                        </>
                      )}
                    </tbody>
                  </table>
                )
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ViewCompanyAddress;
