import {
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  IconButton,
} from "@mui/material";
import LoanIconButton from "../../../../Common/LoanIconButton";
import CompanyModal from "../../../Common/CompanyModal";
// import CompanyModal from "../../../../Common/CompanyModal";
import { RiSkipBackMiniFill } from "react-icons/ri";
import { BsPlus } from "react-icons/bs";
import { BiSkipNext, BiSkipPrevious } from "react-icons/bi";
import { useState } from "react";
import { IoRefresh } from "react-icons/io5";
// import LoanTextField from "../../../Common/LoanTextField";
import CommonServices from "../../Common/CommonServices";
import LoanTextField from "../../../../Common/LoanTextField";
const ViewPreshipmentFacility = ({
  submitted,
  handleBack,
  handleSkip,
  handleNext,
  handleInputChangePreshipmentFacility,
  savePreshipmentFacility,
  preshipmentFacilitys,
  setPreshipmentFacilitys,
}) => {
  const [isCreditPerformanceModalOpen, setIsCreditPerformanceModalOpen] =
    useState(false);
  function handleCreditPerformanceOpenModal() {
    setIsCreditPerformanceModalOpen(true);
  }
  function handlCreditPerformanceCloseModal() {
    setIsCreditPerformanceModalOpen(false);
  }
  const [approvalDate, setApprovalDate] = useState(new Date());
  const [startDate, setStartDate] = useState(new Date());
  const [maturityDate, setMaturityDate] = useState(new Date());
  const [salesContractDate, setSalesContractDate] = useState(new Date());
  const [shipmentDate, setShipmentDate] = useState(new Date());
  const [lOT1ShipmentDate, setLOT1ShipmentDate] = useState(new Date());
  const [LOT1IncomingDate, setLOT1IncomingDate] = useState(new Date()); 
  const [currency, setCurrency] = useState([]);
  const getCurrency = () => {
    CommonServices.getCurrency()
      .then((response) => {
        setCurrency(response.data.Result);
        console.log(response.data.Result);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    getCurrency();
  }, []);
  return (
    <>
      <div className="flex">
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipPrevious />}
          text="Back"
          onClick={handleSkip}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BsPlus />}
          text="Add"
          onClick={handleCreditPerformanceOpenModal}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipNext />}
          text="Skip"
          onClick={handleSkip}
        />
      </div>
      <CompanyModal
        isOpen={isCreditPerformanceModalOpen}
        onClose={handlCreditPerformanceCloseModal}
        title="Add preshipment "
      >
        <div>
          <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            <LoanTextField
              id="ApprovedFacilityLimit"
              label="ApprovedFacilityLimit"
              value={preshipmentFacilitys.ApprovedFacilityLimit}
              onChange={handleInputChangePreshipmentFacility}
              name="ApprovedFacilityLimit"
            />
             <LoanTextField
              id="TypesOfGoods"
              label="TypesOfGoods"
              value={preshipmentFacilitys.TypesOfGoods}
              onChange={handleInputChangePreshipmentFacility}
              name="TypesOfGoods"
            />
             <p>
             Start Date:
              <input
                id="StartDate"
                name="StartDate"
                label="StartDate"
                placeholder="StartDate"
                type="date"
                value={
                  startDate.getFullYear().toString() +
                  "-" +
                  (startDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  startDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setStartDate(new Date(e.target.value));
                  preshipmentFacilitys.StartDate = startDate;
                }}
              />{" "}
            </p>
            <p>
            Maturity Date:
              <input
                id="MaturityDate"
                name="MaturityDate"
                label="MaturityDate"
                placeholder="MaturityDate"
                type="date"
                value={
                  maturityDate.getFullYear().toString() +
                  "-" +
                  (maturityDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  maturityDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setMaturityDate(new Date(e.target.value));
                  preshipmentFacilitys.MaturityDate = maturityDate;
                }}
              />{" "}
            </p>
            <LoanTextField
              id="SalesContractVersion"
              label="SalesContractVersion"
              value={preshipmentFacilitys.SalesContractVersion}
              onChange={handleInputChangePreshipmentFacility}
              name="SalesContractVersion"
            />
             <LoanTextField
              id="SalesContractDocument"
              label="SalesContractDocument"
              value={preshipmentFacilitys.SalesContractDocument}
              onChange={handleInputChangePreshipmentFacility}
              name="SalesContractDocument"
            />
            <p>
            Sales Contract Date:
              <input
                id="SalesContractDate"
                name="SalesContractDate"
                label="SalesContractDate"
                placeholder="SalesContractDate"
                type="date"
                value={
                  salesContractDate.getFullYear().toString() +
                  "-" +
                  (salesContractDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  salesContractDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setSalesContractDate(new Date(e.target.value));
                  preshipmentFacilitys.SalesContractDate = salesContractDate;
                }}
              />{" "}
            </p>
            {/* SectorTypeId
            CurrencyId */}
            <LoanTextField
              id="ContractValue"
              label="ContractValue"
              value={preshipmentFacilitys.ContractValue}
              onChange={handleInputChangePreshipmentFacility}
              name="ContractValue"
            />
            <p>
            Shipment Date:
              <input
                id="ShipmentDate"
                name="ShipmentDate"
                label="ShipmentDate"
                placeholder="ShipmentDate"
                type="date"
                value={
                  shipmentDate.getFullYear().toString() +
                  "-" +
                  (shipmentDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  shipmentDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setShipmentDate(new Date(e.target.value));
                  preshipmentFacilitys.ShipmentDate = shipmentDate;
                }}
              />{" "}
            </p>
            {/* CountryId */}
            {/* IsPartialShipmentAllowed */}
            {/* IsLOTDefined */}
            <LoanTextField
              id="LOT1Value"
              label="LOT1Value"
              value={preshipmentFacilitys.LOT1Value}
              onChange={handleInputChangePreshipmentFacility}
              name="LOT1Value"
            />
            <p>
            LOT Shipment Date:
              <input
                id="LOT1ShipmentDate"
                name="LOT1ShipmentDate"
                label="LOT1ShipmentDate"
                placeholder="LOT1ShipmentDate"
                type="date"
                value={
                  lOT1ShipmentDate.getFullYear().toString() +
                  "-" +
                  (lOT1ShipmentDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  lOT1ShipmentDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setLOT1ShipmentDate(new Date(e.target.value));
                  preshipmentFacilitys.LOT1ShipmentDate = lOT1ShipmentDate;
                }}
              />{" "}
            </p>
            <p>
            LOT Disbursement Date:
              <input
                id="LOT1DisbursementDate"
                name="LOT1DisbursementDate"
                label="LOT1DisbursementDate"
                placeholder="LOT1DisbursementDate"
                type="date"
                value={
                  lOT1DisbursementDate.getFullYear().toString() +
                  "-" +
                  (lOT1DisbursementDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  lOT1DisbursementDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setLOT1DisbursementDate(new Date(e.target.value));
                  preshipmentFacilitys.LOT1DisbursementDate = lOT1DisbursementDate;
                }}
              />{" "}
            </p>
            <LoanTextField
