import { useState, useEffect } from "react";
import CompanyModal from "../../Common/CompanyModal";
import LoanTextField from "../../../Common/LoanTextField";
import { Select, FormControl, MenuItem, InputLabel } from "@mui/material";
import AddMaritalStatus from "./AddMaritalStatus";
import CommonServices from "../../Common/CommonServices";
const ViewMaritalStatus = ({
  holdMarriedTax,
  SoleId,
  isCompanyMarriedTaxModalOpen,
  handleCompanyMarriedTaxCloseModal,
  handleMarriedTaxModalOpen,
}) => {
  const [marriedId, setMarriedId] = useState();
  const [country, setContry] = useState([]);
  const [toggleMarriedTax, setToggleMarriedTax] = useState(false);
  const [proofOfIdentityType, setProofOfIdentityType] = useState([]);
  const [
    isCompanyMarriedTaxEditModalOpen,
    setIsCompanyMarriedTaxEditModalOpen,
  ] = useState(false);
  function handleCompanyMarriedTaxEditCloseModal() {
    setIsCompanyMarriedTaxEditModalOpen(false);
  }
  function handleCompanyMarriedTaxEditOpenModal() {
    setIsCompanyMarriedTaxEditModalOpen(true);
  }
  function handleCompanyMarriedTaxEditCloseModal() {
    setIsCompanyMarriedTaxEditModalOpen(false);
  }
  const [taxInformationId, setTaxInformationId] = useState();
  const [editMarriedTax, setEditMarriedTax] = useState({
    Id: taxInformationId,
    Name: "",
    TinNo: "",
    IssueDate: "",
    Expireddate: "",
    TaxDebi: "",
    TaxClearanceCertificate: "",
  });
  const handleInputChangeEditMarriedTax = (event) => {
    const { name, value } = event.target;
    setEditMarriedTax({ ...editMarriedTax, [name]: value });
  };
  const updateMarriedTaxById = (taxInformationId) => {
    CommonServices.getMarriedTax(taxInformationId)
      .then((res) => {
        setEditMarriedTax({
          ...editMarriedTax,
          Id: taxInformationId,
          Name: res.data.Result.Name,
          TinNo: res.data.Result.TinNo,
          IssueDate: res.data.Result.IssueDate,
          Expireddate: res.data.Result.Expireddate,
          TaxDebi: res.data.Result.TaxDebi,
          TaxClearanceCertificate: res.data.Result.TaxClearanceCertificate,
        });
      })
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    updateMarriedTaxById();
  }, []);
  const updateMarriedTax = () => {
    CommonServices.updateMarriedTax(taxInformationId, editMarriedTax)
      .then((response) => {})
      .catch((e) => {
        console.log(e);
      });
  };
  useEffect(() => {
    updateMarriedTax();
  }, []);
  return (
    <div>
      <CompanyModal
        isOpen={isCompanyMarriedTaxModalOpen}
        onClose={handleCompanyMarriedTaxCloseModal}
        title="view married Tax information"
      >
        <div className="rounded-lg overflow-hidden">
          {holdMarriedTax ? (
            <table className="table table-bordered table-striped bg-purple-100">
              <thead>
              <th className="px-2 py-4"> Name </th>
                            <th className="px-2 py-4"> TinNo </th>
                            <th className="px-2 py-4"> Issue Date </th>
                            <th className="px-2 py-4"> Expired date </th>
                            <th className="px-2 py-4"> Tax Debi </th>
                            <th className="px-2 py-4">
                              Tax Clearance Certificate
                            </th>
              </thead>
              <tbody>
                <tr>   
                  <td>{holdMarriedTax.Name}</td>
                  <td>{holdMarriedTax.TinNo}</td>
                  <td>{holdMarriedTax.IssueDate}</td>
                  <td>{holdMarriedTax.Expireddate}</td>
                  <td>{holdMarriedTax.TaxDebi}</td>
                  <td>{holdMarriedTax.TaxClearanceCertificate}</td>
                  <td>
                    <button
                      onClick={() => {
                        handleCompanyMarriedTaxEditOpenModal();
                        updateMarriedTaxById(holdMarriedTax.Id);
                        setTaxInformationId(holdMarriedTax.Id);
                        // saveTaxSoleInfo(sole.Id);
                      }}
                      className="text-purple-600 rounded-md font-semibold "
                    >
                      Edit
                    </button>
                  </td>
                  <td>
                    <button
                      type="submit"
                      onClick={() => {}}
                      className="text-purple-600 rounded-md font-semibold "
                    >
                      Marital status detail
                    </button>
                  </td>
                  <td>
                    <button className="text-purple-600 rounded-md font-semibold ">
                      Tax detail
                    </button>
                  </td>
                </tr>
              </tbody>
            </table>
          ) : (
            ""
          )}
        </div>
        <div>
          {holdMarriedTax <= 0 && (
            <AddMaritalStatus Add married tax status information SoleId={SoleId} />
          )}
        </div>
      </CompanyModal>
      <CompanyModal
        isOpen={isCompanyMarriedTaxEditModalOpen}
        onClose={handleCompanyMarriedTaxEditCloseModal}
        title="tax edit information"
      >
        <div>
          <form onSubmit={updateMarriedTax}>
            <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              <LoanTextField
                id="FirstName"
                label="FirstName"
                value={editMarriedTax.Name}
                onChange={handleInputChangeEditMarriedTax}
                name="FirstName"
              />
              <LoanTextField
                id="MiddleName"
                label="MiddleName"
                value={editMarriedTax.TinNo}
                onChange={handleInputChangeEditMarriedTax}
                name="MiddleName"
              />
              <LoanTextField
                id="LastName"
                label="LastName"
                value={editMarriedTax.IssueDate}
                onChange={handleInputChangeEditMarriedTax}
                name="LastName"
              />
              <LoanTextField
                id="ProofOfIdentity"
                label="ProofOfIdentity"
                value={editMarriedTax.Expireddate}
                onChange={handleInputChangeEditMarriedTax}
                name="ProofOfIdentity"
              />
              <LoanTextField
                id="MarriageDate"
                label="MarriageDate"
                value={editMarriedTax.TaxDebi}
                onChange={handleInputChangeEditMarriedTax}
                name="MarriageDate"
              />
              <LoanTextField
                id="MarriageCertificate"
                label="MarriageCertificate"
                value={editMarriedTax.TaxClearanceCertificate}
                onChange={handleInputChangeEditMarriedTax}
                name="MarriageCertificate"
              />
              
            </div>
            <div>
              <button className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                Update
              </button>
            </div>
          </form>
        </div>
      </CompanyModal>
    </div>
  );
};
export default ViewMaritalStatus;
