import { useParams, useNavigate } from "react-router-dom";
import { LiaGreaterThanSolid } from "react-icons/lia";
import SpecificEligibilityLoanData from '../Data/SpecificEligibilityLoanData.json';
import EligibilityCriteria from "../EligibilityCriteria/SpecificEligibilityCriteria";
import { useState } from "react";
import GeneralEligililityCriteriaLoanData from "../Data/GeneralEligililityCriteriaLoanData.json";
import HorizontalTabs from './HorizontalTabs';
import LoanModal from '../Modal/LoanModal';

const ProductDetail = () => {
    const { baseLine, groupId, productId } = useParams();
    const navigate = useNavigate();
    const productBaseLine = SpecificEligibilityLoanData.product_line.filter(productLine => productLine.id == baseLine);
    const [isGeneralModalOpen, setIsGeneralModalOpen] = useState(false)
    const [generalEligibilityCriteria, setGeneralEligibilityCriteria] = useState("");

    function handleGeneralOpenModal() {
        setIsGeneralModalOpen(true)
    }

    function handleGeneralCloseModal() {
        setIsGeneralModalOpen(false)
    }

    const handleGoProductGroup = () => {
        navigate(`/product/${baseLine}/${groupId}`);
    }
    const handleGoProductLine = () => {
        navigate(`/`);
    }
    const doYouHaveAccount = () => {
        const confirmed = window.confirm('Do you have account on CBE Loan?');
        if (confirmed) {
            // Navigate to login
            navigate('/login');
        }
        else {
            // Navigate to signup page
            return navigate('/registrationPage');
        }
    }
    return (
        <div>
            <div>
                <header className="px-4 bg-gray-50 border border-gray-200 p-2 space-x-2">
                    <button className="text-purple-600 underline hover:font-semibold inline capitalize" data-toggle="tooltip" title="Click it go back" onClick={handleGoProductLine}>
                        {
                            productBaseLine.map(productLine => productLine.name)
                        }
                    </button>
                    <div className="inline">
                        <LiaGreaterThanSolid className="text-purple-600 inline" />
                    </div>
                    <div className="inline">
                        <button className="text-purple-600 capitalize underline hover:font-semibold visited:text-purple-650" data-toggle="tooltip" title="Click it go back" onClick={handleGoProductGroup}>
                            {
                                productBaseLine.map(pr => (pr.group.filter(gr => gr.id == groupId)).map(selectedGroup => (selectedGroup.name)))
                            }
                        </button>
                    </div>
                    <div className="inline">
                        <LiaGreaterThanSolid className="text-purple-600 inline" />
                    </div>
                    <div className="inline">
                        <button className="text-purple-600 capitalize underline hover:font-semibold visited:text-purple-650" data-toggle="tooltip" title="Click it go back">
                            {
                                productBaseLine.map(pr => (pr.group.filter(gr => gr.id == groupId)).map(selectedGroup => (selectedGroup.product.filter(pro => pro.id == productId)).map(selectedProduct => selectedProduct.name)))
                            }
                        </button>
                    </div>
                </header>
                <header className="px-2 bg-gray-50 border border-purple-200 p-2 space-x-2">
                    <button onClick={() => { handleGeneralOpenModal(); setGeneralEligibilityCriteria(GeneralEligililityCriteriaLoanData.map(general => general.requiredDocuments)) }}
                        className="text-purple-600 inline w-[200px] rounded-md font-medium text-center">
                        <span> General Eligibility Criteria </span>
                    </button>
                </header>
            </div>
            {
                generalEligibilityCriteria && (
                    <LoanModal isOpen={isGeneralModalOpen} onClose={handleGeneralCloseModal} title="General eligibility criteria">
                        <HorizontalTabs criteria={generalEligibilityCriteria} />
                        {/* {generalEligibilityCriteria} */}
                    </LoanModal>
                )
            }
            <section>
                <article>
                    <div className="mx-w-[1240px] mx-auto grid gap-1 ease-in-out px-4">
                        {
                            productBaseLine.map(pr => (pr.group.filter(gr => gr.id == groupId)).map(selectedGroup => (selectedGroup.product.filter(pro => pro.id == productId)).map((selectedProduct) => (
                                <div className="w-full border-2 shadow-xl flex flex-col my-4 rounded-lg">
                                    <header className="text-2xl bg-[purple] text-white ease-in-out border text-center capitalize">
                                        {selectedProduct.name} Specific Eligibility Criteria
                                    </header>
                                    <div className="px-8 text-xl items-center justify-center">
                                        {
                                            selectedProduct.specificEligibilityCriteria.length > 0 ? (
                                                <EligibilityCriteria criteria={selectedProduct.specificEligibilityCriteria} />
                                            ) : ""
                                        }
                                        <button className="block bg-[purple] w-[150px] rounded-md font-medium my-6 text-white mx-auto py-2 text-center" onClick={doYouHaveAccount}>Apply</button>
                                    </div>
                                </div>
                            ))))

                        }
                    </div>
                </article>
            </section>
        </div>
    );
}

export default ProductDetail;
