import {
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  IconButton,
} from "@mui/material";
import { BsPlus } from "react-icons/bs";
import { BiSkipNext } from "react-icons/bi";
import CompanyModal from "../Common/CompanyModal";
import { useState } from "react";
import LoanTextField from "../../Common/LoanTextField";
import LoanIconButton from "../../Common/LoanIconButton";
import { IoRefresh } from "react-icons/io5";
import CommonServices from "../Common/CommonServices";
const AddSeniorManagementStaff = ({
  seniorManagementStaff,
  handleInputChange,
  saveSeniorStaff,
  handleSkip,
  onClick,
  setSelectedCVDocument,
  setSelectedWorkExperienceDocument,
}) => {
  const [
    isSeniorManagementStaffModalOpen,
    setIsSeniorManagementStaffModalOpen,
  ] = useState(false);

  function handleSeniorManagementStaffCloseModal() {
    setIsSeniorManagementStaffModalOpen(false);
  }
  function handleSeniorManagementStaffOpenModal() {
    setIsSeniorManagementStaffModalOpen(true);
  }
  //upload

  const [selectedFiles, setSelectedFiles] = useState(undefined);
  const [currentFile, setCurrentFile] = useState(undefined);
  const [fileInfos, setFileInfos] = useState([]);
  const [progress, setProgress] = useState(0);
  const [message, setMessage] = useState("");
  const selectFile = (event) => {
    setSelectedFiles(event.target.files);
  };
  //upload 1
  const uploadCVDocument = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    CommonServices.upload(currentFile, (event) => {
      setProgress(Math.round((100 * event.loaded) / event.total));
    })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedCVDocument(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //end upload
  //upload 2
  const uploadWorkExperienceDocument = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    CommonServices.upload(currentFile, (event) => {
      setProgress(Math.round((100 * event.loaded) / event.total));
    })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedWorkExperienceDocument(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //endupload2
  return (
    <>
      <div className="flex">
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BsPlus />}
          text="Add"
          onClick={handleSeniorManagementStaffOpenModal}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipNext />}
          text="Skip"
          onClick={handleSkip}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<IoRefresh />}
          text="Reload"
          onClick={onClick}
        />

<Link to={`/trade/${companyId}`}>
          <LoanIconButton
            style={{ color: "purple" }}
            icon={<BiSkipPrevious />}
            text="Trade Registartion"
            onClick={handlePrevios}
          />
        </Link>
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BsPlus />}
          text="Add"
          onClick={handleCompanyEmployeeSoleOpenModal}
        />
        <Link to={`/enterprise/senior-and-management-staff/${companyId}`}>
          <LoanIconButton
            style={{ color: "purple" }}
            icon={<BiSkipNext />}
            text="Senior Management Staff"
            onClick={handleSkip}
          />
        </Link>
      </div>
      <CompanyModal
        isOpen={isSeniorManagementStaffModalOpen}
        onClose={handleSeniorManagementStaffCloseModal}
        title="Add senior management staff"
      >
        <div className="grid grid-cols-1 gap-4 submit-form">
          <form onSubmit={saveSeniorStaff}>
            <div
              className="relative center justify-center
flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4"
            >
              <LoanTextField
                id="FullName"
                label="Full Name"
                value={seniorManagementStaff.FullName}
                onChange={handleInputChange}
                name="FullName"
              />
              <LoanTextField
                id="TotalWorkExperience"
                label="Total Work Experience"
                type="number"
                value={seniorManagementStaff.TotalWorkExperience}
                onChange={handleInputChange}
                name="TotalWorkExperience"
              />
              <LoanTextField
                id="RelatedWorkExperience"
                label="Related Work Experience"
                value={seniorManagementStaff.RelatedWorkExperience}
                onChange={handleInputChange}
                name="RelatedWorkExperience"
              />
              <LoanTextField
                id="Position"
                label="Position"
                value={seniorManagementStaff.Position}
                onChange={handleInputChange}
                name="Position"
              />
              <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  disabled={!selectedFiles}
                  onClick={uploadCVDocument}
                >
                  upload CV Document
                </button>
                <div className="alert alert-light" role="alert">
                  {message}
                </div>
              </div>
              {/* <LoanTextField
                id="CVDocument"
                label="CV Document"
                value={seniorManagementStaff.CVDocument}
                onChange={handleInputChange}
                name="CVDocument"
              /> */}
              <div>
                {currentFile && (
                  <div className="progress">
                    <div
                      className="progress-bar progress-bar-info progress-bar-striped"
                      role="progressbar"
                      aria-valuenow={progress}
                      aria-valuemin="0"
                      aria-valuemax="100"
                      style={{ width: progress + "%" }}
                    >
                      {progress}%
                    </div>
                  </div>
                )}
                <label className="btn btn-default">
                  <input type="file" onChange={selectFile} />
                </label>
                <button
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  disabled={!selectedFiles}
                  onClick={uploadWorkExperienceDocument}
                >
                  Work Experience Document
                </button>
                <div className="alert alert-light" role="alert">
                  {message}
                </div>
              </div>
              {/* <LoanTextField
                id="WorkExperienceDocument"
                label="Work Experience Document"
                value={seniorManagementStaff.WorkExperienceDocument}
                onChange={handleInputChange}
                name="WorkExperienceDocument"
              /> */}
            </div>
            <div className="flex justify-center pt-4">
              <button className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                Save
              </button>
            </div>
          </form>
        </div>
      </CompanyModal>
    </>
  );
};

export default AddSeniorManagementStaff;
