import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import loginDataService from "./Service";

const Login = () => {
    const navigate = useNavigate();
    const [PhoneNumber, setPhoneNumber] = useState("");
    const [Password, setPassword] = useState("");

    const [showForgotPasswordPopup, setShowForgotPasswordPopup] = useState(false);

    const [phoneEntered, setPhoneEntered] = useState(false);
    const [otp, setOtp] = useState("");

    const handleApiAuthentication = async (PhoneNumber, Password) => {
        try {
            const response = await axios.post(
                "https://10.3.6.135/api/v1/user/authenticate",
                {
                    PhoneNumber,
                    Password,
                }
            );
            // The response contains the user authentication token or other information
            // that you can use to set the user session or redirect to another page.
            console.log(response.data);
        } catch (error) {
            // Handle authentication error
            console.error(error);
        }
    };
    const handleSubmit = (event) => {
        event.preventDefault();
        handleApiAuthentication(PhoneNumber, Password);
        navigate("/company/1");
    };

    const handleForgotPasswordClick = () => {
        setShowForgotPasswordPopup(true);
    };

    const handlePopupClose = () => {
        setShowForgotPasswordPopup(false);
        setPhoneEntered(false);
        setOtp("");
    };

    const handleSendOtpClick = () => {
        // Send OTP to phone number
        // After OTP is sent, you can set another state to indicate that the OTP has been sent
    };

    const handleVerifyClick = () => {
        //  Verify OTP
        // If OTP is verified successfully, you can redirect the user to a password reset page
    };

    const handlePhoneChange = (event) => {
        // Validate phone number input
        setPhoneEntered(true);
    };

    const handleOtpChange = (event) => {
        // TODO: Validate OTP input
        setOtp(event.target.value);
    };

    return (
        <>
            <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8">
                <div className="sm:mx-auto sm:w-full sm:max-w-sm">
                    <h2 className="mt-10 text-center text-2xl font-bold leading-9 tracking-tight text-gray-1000">
                        Sign in to your account
                    </h2>
                </div>

                <div className="mt-10 sm:mx-auto sm:w-full sm:max-w-sm">
                    <form className="space-y-6" onSubmit={handleSubmit}>
                        <div>
                            <label
                                htmlFor="phoneNumber"
                                className="block text-sm font-medium leading-6 text-gray-900"
                            >
                                Phone number
                            </label>
                            <div className="mt-2">
                                <input
                                    id="PhoneNumber"
                                    name="PhoneNumber"
                                    type="tel"
                                    autoComplete="tel"
                                    required
                                    className="block w-full rounded-md border-0 py-1.5 text-gray-900 pl-2 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                                    value={PhoneNumber}
                                    onChange={(event) => setPhoneNumber(event.target.value)}
                                />
                            </div>
                        </div>

                        <div>
                            <div className="flex items-center justify-between">
                                <label
                                    htmlFor="password"
                                    className="block text-sm font-medium leading-6 text-gray-900"
                                >
                                    Password
                                </label>
                                <div className="text-sm">
                                    <button
                                        type="button"
                                        onClick={handleForgotPasswordClick}
                                        className="font-semibold text-fuchsia-700 hover:text-fuchsia-900"
                                    >
                                        Forgot password?
                                    </button>
                                </div>
                            </div>
                            <div className="mt-2">
                                <input
                                    id="Password"
                                    name="Password"
                                    type="Password"
                                    autoComplete="current-password"
                                    required
                                    className="block w-full rounded-md border-0 py-1.5 text-gray-900 pl-2 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
                                    value={Password}
                                    onChange={(event) => setPassword(event.target.value)}
                                />
                            </div>
                        </div>

                        <div>
                            <button
                                type="submit"
                                className="flex w-full justify-center rounded-md bg-fuchsia-700 px-3 py-1.5 text-sm font-semibold leading-6 text-white shadow-sm hover:bg-fuchsia-900 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
                            >
                                Sign in
                            </button>
                        </div>
                    </form>

                    <p className="mt-10 text-center text-sm text-gray-500">
                        Not a member?{" "}
                        <Link
                            to="/registrationPage"
                            className="font-semibold leading-6 text-fuchsia-700 hover:text-yellow-500"
                        >
                            Sign Up
                        </Link>
                    </p>

                    {showForgotPasswordPopup && (
                        <div className="fixed z-50 inset-1 overflow-y flex items-center justify-center">
                            <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
                                <div className="fixed inset-0 transition-opacity">
                                    <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
                                </div>
                                <span className="hidden sm:inline-block sm:align-middle sm:h-screen"></span>
                                &#8203;
                                <div
                                    className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full"
                                    role="dialog"
                                    aria-modal="true"
                                    aria-labelledby="modal-headline"
                                >
                                    <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                                        {!phoneEntered && (
                                            <>
                                                <div className="sm:flex sm:items-start">
                                                    <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left">
                                                        <h3
                                                            className="text-lg leading-6 font-medium text-gray-900"
                                                            id="modal-headline"
                                                        >
                                                            Forgot your password?
                                                        </h3>
                                                        <div className="mt-2">
                                                            <p className="text-sm leading-5 text-gray-500">
                                                                Enter the phone number associated with your
                                                                account and we'll send you a verification code
                                                                to reset your password.
                                                            </p>
                                                        </div>
                                                    </div>
                                                </div>

                                                <div className="mt-5">
                                                    <input
                                                        type="tel"
                                                        name="phone"
                                                        id="phone"
                                                        autoComplete="tel"
                                                        required
                                                        className="block w-full rounded-md border-gray-300 shadow-sm py-2 px-3 focus:outline-none focus:ring-fuchsia-500 focus:border-fuchsia-500 sm:text-sm sm:leading-5"
                                                        placeholder="Phone number"
                                                        onChange={handlePhoneChange}
                                                    />
                                                </div>

                                                <div className="mt-5 sm:mt-6">
                                                    <span className="flex w-full rounded-md shadow-sm">
                                                        <button
                                                            type="button"
                                                            className="inline-flex justify-center w-full rounded-md border border-transparent px-4 py-2 bg-fuchsia-600 text-base leading-6 font-medium text-white shadow-sm hover:bg-fuchsia-500 focus:outline-none focus:border-fuchsia-700 focus:shadow-outline-indigo transition ease-in-out duration-150 sm:text-sm sm:leading-5"
                                                            onClick={handleSendOtpClick}
                                                        >
                                                            Send verification code
                                                        </button>
                                                    </span>
                                                </div>
                                            </>
                                        )}

                                        {phoneEntered && (
                                            <>
                                                <div className="sm:flex sm:items-start">
                                                    <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left">
                                                        <h3
                                                            className="text-lg leading-6 font-medium text-gray-900"
                                                            id="modal-headline"
                                                        >
                                                            Enter verification code
                                                        </h3>
                                                        <div className="mt-4">
                                                            <p className="text-sm leading-5 text-gray-500">
                                                                We've sent a verification code to your phone
                                                                number. Please enter it below to reset your
                                                                password.
                                                            </p>{" "}
                                                        </div>{" "}
                                                    </div>
                                                    <div className="mt-5">
                                                        <input
                                                            type="text"
                                                            name="otp"
                                                            id="otp"
                                                            autoComplete="one-time-code"
                                                            required
                                                            className="block w-full rounded-md border-gray-300 shadow-sm py-2 px-3 focus:outline-none focus:ring-fuchsia-500 focus:border-fuchsia-500 sm:text-sm sm:leading-5"
                                                            placeholder="Verification code"
                                                            value={otp}
                                                            onChange={handleOtpChange}
                                                        />
                                                    </div>

                                                    <div className="mt-5 sm:mt-6">
                                                        <span className="flex w-full rounded-md shadow-sm">
                                                            <button
                                                                type="button"
                                                                className="inline-flex justify-center w-full rounded-md border border-transparent px-4 py-2 bg-fuchsia-600 text-base leading-6 font-medium text-white shadow-sm hover:bg-fuchsia-500 focus:outline-none focus:border-fuchsia-700 focus:shadow-outline-indigo transition ease-in-out duration-150 sm:text-sm sm:leading-5"
                                                                onClick={handleVerifyClick}
                                                            >
                                                                Verify
                                                            </button>
                                                        </span>
                                                    </div>
                                                </div>
                                            </>
                                        )}
                                    </div>

                                    <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
                                        <span className="flex w-full rounded-md shadow-sm sm:ml-3 sm:w-auto">
                                            <button
                                                type="button"
                                                className="inline-flex justify-center w-full rounded-md border border-transparent px-4 py-2 bg-fuchsia-600 text-base leading-6 font-medium text-white shadow-sm hover:bg-fuchsia-500 focus:outline-none focus:border-fuchsia-700 focus:shadow-outline-indigo transition ease-in-out duration-150 sm:text-sm sm:leading-5"
                                                onClick={handlePopupClose}
                                            >
                                                Close
                                            </button>
                                        </span>

                                        <span className="mt-3 flex w-full rounded-md shadow-sm sm:mt-0 sm:w-auto">
                                            <button
                                                type="button"
                                                className="inline-flex justify-center w-full rounded-md border border-transparent px-4 py-2 bg-fuchsia-600 text-base leading-6 font-medium text-white shadow-sm hover:bg-fuchsia-500 focus:outline-none focus:border-fuchsia-700 focus:shadow-outline-indigo transition ease-in-out duration-150 sm:text-sm sm:leading-5"
                                                onClick={handleSendOtpClick}
                                            >
                                                Send again
                                            </button>
                                        </span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </>
    );
};

export default Login;
