import {
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  IconButton,
} from "@mui/material";
import LoanIconButton from "../../../Common/LoanIconButton";
import CompanyModal from "../../Common/CompanyModal";
import { RiSkipBackMiniFill } from "react-icons/ri";
import { BsPlus } from "react-icons/bs";
import { BiSkipNext, BiSkipPrevious } from "react-icons/bi";
import { useState } from "react";
import { IoRefresh } from "react-icons/io5";
import LoanTextField from "../../../Common/LoanTextField";
const AddDepositPerformance = ({
  // submitted,
  // handleBack,
  handleSkip,
  // handleNext,
  // newAccountDetials,
  setDepositePerformances,
  depositePerformances,
  handleInputChangeDepositePerformance,
  currency,
  saveDepositPerformance,
  setCurrencyId,
}) => {
 

  const [isDepositPerformanceModalOpen, setIsDepositPerformanceModalOpen] =
  useState(false);
function handleDepositPerformanceOpenModal() {
  setIsDepositPerformanceModalOpen(true);
}
function handleDepositPerformanceCloseModal() {
  setIsDepositPerformanceModalOpen(false);
}

  const [approvalDate, setApprovalDate] = useState(new Date());
  const [startDate, setStartDate] = useState(new Date());
  const [maturityDate, setMaturityDate] = useState(new Date());
  const [scheduledPayment, setScheduledPayment] = useState(new Date());
  const [
    systemAdjustmentDate4WaiverAndExtension,
    setSystemAdjustmentDate4WaiverAndExtension,
  ] = useState(new Date());
  const [adjustmentMaturityDate, setAdjustmentMaturityDate] = useState(
    new Date()
  );
  return (
    <>
      <div className="flex">
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipPrevious />}
          text="Back"
          onClick={handleSkip}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BsPlus />}
          text="Add"
          onClick={handleDepositPerformanceOpenModal}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipNext />}
          text="Skip"
          onClick={handleSkip}
        />
      </div>
      <CompanyModal
        isOpen={{ isDepositPerformanceModalOpen }}
        onClose={{ handlDepositPerformanceCloseModal }}
        title="Add handle Credit deposite performance "
      >
        <div>
          <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
            <LoanTextField
              id="BudgetYear"
              label="BudgetYear"
              value={depositePerformances.BudgetYear}
              onChange={handleInputChangeDepositePerformance}
              name="BudgetYear"
            />
            <p>
              Approval Date:
              <input
                id="StartDate"
                name="StartDate"
                label="StartDate"
                placeholder="StartDate"
                type="date"
                value={
                  approvalDate.getFullYear().toString() +
                  "-" +
                  (approvalDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  approvalDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setApprovalDate(new Date(e.target.value));
                  depositePerformances.StartDate = approvalDate;
                }}
              />{" "}
            </p>
            <p>
              End Date:
              <input
                id="EndDate"
                name="EndDate"
                label="EndDate"
                placeholder="EndDate"
                type="date"
                value={
                  approvalDate.getFullYear().toString() +
                  "-" +
                  (approvalDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  approvalDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setApprovalDate(new Date(e.target.value));
                  depositePerformances.EndDate = approvalDate;
                }}
              />{" "}
            </p>
            <FormControl variant="standard" sx={{ minWidth: 120 }}>
              <InputLabel id="CurrencyId" color="secondary">
                Currency{" "}
              </InputLabel>
              <Select
                color="secondary"
                onChange={(event) => {
                  depositePerformances.CurrencyId = event.target.value;
                  setCurrencyId(event.target.value);
                }}
                labelId="CurrencyId"
                id="CurrencyId"
                label="CurrencyId"
              >
                <MenuItem value="">
                  <em>None</em>
                </MenuItem>
                {currency.map((yourLoan) => (
                  <MenuItem
                    key={yourLoan.Id}
                    CurrencyId={yourLoan.Id}
                    value={yourLoan.Id}
                  >
                    {yourLoan.Name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <LoanTextField
              id="DepositeAccount"
              label="DepositeAccount"
              value={depositePerformances.DepositeAccount}
              onChange={handleInputChangeDepositePerformance}
              name="DepositeAccount"
            />
            <LoanTextField
              id="DepositBalance"
              label="DepositBalance"
              value={depositePerformances.DepositBalance}
              onChange={handleInputChangeDepositePerformance}
              name="DepositBalance"
            />
            <LoanTextField
              id="AverageMonthlyDepositPerAnnum"
              label="AverageMonthlyDepositPerAnnum"
              value={depositePerformances.AverageMonthlyDepositPerAnnum}
              onChange={handleInputChangeDepositePerformance}
              name="AverageMonthlyDepositPerAnnum"
            />
            <LoanTextField
              id="TotalCreditTransactionAmountPerAnnum"
              label="TotalCreditTransactionAmountPerAnnum"
              value={depositePerformances.TotalCreditTransactionAmountPerAnnum}
              onChange={handleInputChangeDepositePerformance}
              name="TotalCreditTransactionAmountPerAnnum"
            />
            {/* ApprovalDate */}
            <p>
              AccountOpeningDate:
              <input
                id="AccountOpeningDate"
                name="AccountOpeningDate"
                label="AccountOpeningDate"
                placeholder="AccountOpeningDate"
                type="date"
                value={
                  approvalDate.getFullYear().toString() +
                  "-" +
                  (approvalDate.getMonth() + 1).toString().padStart(2, 0) +
                  "-" +
                  approvalDate.getDate().toString().padStart(2, 0)
                }
                onChange={(e) => {
                  setApprovalDate(new Date(e.target.value));
                  depositePerformances.AccountOpeningDate = approvalDate;
                }}
              />{" "}
            </p>
            <LoanTextField
              id="TotalCreditTransactionNumberPerAnnum"
              label="TotalCreditTransactionNumberPerAnnum"
              value={depositePerformances.TotalCreditTransactionNumberPerAnnum}
              onChange={handleInputChangeDepositePerformance}
              name="TotalCreditTransactionNumberPerAnnum"
            />
            <LoanTextField
              id="DepositSalesRatio"
              label="DepositSalesRatio"
              value={depositePerformances.DepositSalesRatio}
              onChange={handleInputChangeDepositePerformance}
              name="DepositSalesRatio"
            />
          </div>
          <div className="flex justify-center p-2 pt-5">
            <button
              className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              onClick={() => {
                saveDepositPerformance();
              }}
            >
              Save
            </button>
          </div>
        </div>
      </CompanyModal>
    </>
  );
};
export default AddDepositPerformance;
