import { useState, useEffect } from "react";
import {
  BsArrowLeftShort,
  BsChevronDown,
  BsPlus,
  BsSearch,
} from "react-icons/bs";
import { AiFillEnvironment } from "react-icons/ai";
import { RiDashboardFill } from "react-icons/ri";
import { GrProjects } from "react-icons/gr";
import { GiProgression } from "react-icons/gi";
import { FcOrganization } from "react-icons/fc";
import cbeLogo from "../../Images/favicon.ico";
import Tooltips from "../../Tooltip/Tooltips";
import "./style.css";
import { Link, Navigate } from "react-router-dom";
import { useParams, useNavigate } from "react-router-dom";
import Basic from "../../Company/Basic";
import classNames from "classnames";
import { TextField } from "@mui/material";
import loanDataService from "../../Company/Service";
import { InputLabel, MenuItem, FormControl, Select } from "@mui/material";
// import { BsArrowLeftShort, BsSearch, BsChevronDown, BsPlus } from "react-icons/bs";
import CompanyModal from "./CompanyModal";
import { IconButton } from "@mui/material";
import { BsWindowPlus } from "react-icons/bs";
import { BiSkipNext } from "react-icons/bi";
import { RiSkipBackMiniFill } from "react-icons/ri";
import { useForm } from "react-hook-form";
import { useUploadFile } from "dex-react-file-upload";
const LeftNav = () => {
  let navigate = useNavigate();
  const { Id } = useParams();

  const initialCompanyState = {
    Id: null,
    Name: "",
    NoOfBranch: "",
    EstablishmentYear: "",
    TINNumber: "",
    TINCertificate: "",
    CompanyTypeID: "",
    EntityTypeID: "",
    VATNumber: "",
    VATCertificate: "",
  };
  //realted to martial status
  const [martailStatus, setMartailStatus] = useState();
  const [martailStatusHold, setMartailStatusHold] = useState();
  const [martailStatusHoldSetData, setMartailStatusHoldSetData] = useState([]);
  // debugger;

  //end maertial status
  //related to upload file
  const [selectedFiles, setSelectedFiles] = useState(undefined);
  // const [folderName, setFolderName] = useState("");
  // debugger;
  const [currentFile, setCurrentFile] = useState(undefined);
  const [progress, setProgress] = useState(0);
  const [message, setMessage] = useState("");
  const [fileInfos, setFileInfos] = useState([]);
  const [selectedPathLin, setSelectedPathLin] = useState();
  const [selectedPathTRN, setSelectedPathTRN] = useState();
  const [selectedPathLinEdit, setSelectedPathLinEdit] = useState();
  const [selectedPathTRNEdit, setSelectedPathTRNEdit] = useState();
  const [toggleTaxSoleInfo, setToggleTaxInfo] = useState(false);
  const [toggleGruantorSoleInfo, setToggleGruantorInfo] = useState(false);
  const [toggleMarriedInfo, setToggleMarriedInfo] = useState(false);
  //related to registration number
  //NGO
  const [selectedPathPL, setSelectedPathPL] = useState();
  const [selectedPathLD, setSelectedPathLD] = useState();
  const [selectedPathPLEdit, setSelectedPathPLEdit] = useState();
  const [selectedPathLDEdit, setSelectedPathLDEdit] = useState();
  const [getNGOAndEmbassyInformation, setGetNGOAndEmbassyInformation] =
    useState();
  const [ngoAndEmbassyInformationId, setNGOAndEmbassyInformationId] =
    useState();
  const [NGOAndEmbassyInfoIdForLocal, setNGOAndEmbassyInfoIdForLocal] =
    useState();
  const [editNGOAndEmbassyInfo, setEditNGOAndEmbassyInfo] = useState({
    Id: ngoAndEmbassyInformationId,
    AuthRefNo: "",
    AuthDate: "",
    UploadPermisionLetter: selectedPathPLEdit,
    UploadParentCompanyLegalDocument: selectedPathLDEdit,
  });
  const selectFile2Edit = (event) => {
    setSelectedFiles(event.target.files);
  };
  const upload2Edit = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathPLEdit(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };

  const selectFile3Edit = (event) => {
    setSelectedFiles(event.target.files);
  };
  const upload3Edit = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathLDEdit(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  //ENDE EDIT NGO
  //file upload NGO
  const selectFile2 = (event) => {
    setSelectedFiles(event.target.files);
  };
  const upload2 = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathPL(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };

  const selectFile3 = (event) => {
    setSelectedFiles(event.target.files);
  };
  const upload3 = () => {
    let currentFile = selectedFiles[0];
    setProgress(0);
    setCurrentFile(currentFile);
    loanDataService
      .upload(currentFile, (event) => {
        setProgress(Math.round((100 * event.loaded) / event.total));
      })
      .then((response) => {
        setMessage(response.data.message);
        setSelectedPathLD(response.data.Result);
      })
      .then((files) => {
        setFileInfos(files.data);
      })
      .catch(() => {
        setProgress(0);
        // setMessage("Could not upload the file!");
        setCurrentFile(undefined);
      });
    setSelectedFiles(undefined);
  };
  const initialNGOAndEmbassyInfoState = {
    Id: null,
    AuthRefNo: "",
    AuthDate: "",
    UploadPermisionLetter: "",
    UploadParentCompanyLegalDocument: "",
  };
  const getNGOAndEmbassyInformationBy = (ngoAndEmbassyInformationId) => {
    loanDataService
      .getNGOAndEmbassyInformationByNGOId(ngoAndEmbassyInformationId)
      .then((res) => {
        setEditNGOAndEmbassyInfo({
          ...editNGOAndEmbassyInfo,
          Id: ngoAndEmbassyInformationId,
          AuthRefNo: res.data.Result.AuthRefNo,
          AuthDate: res.data.Result.AuthDate,
          UploadPermisionLetter: res.data.Result.UploadPermisionLetter,
          UploadParentCompanyLegalDocument:
            res.data.Result.UploadParentCompanyLegalDocument,
        });
        // debugger;
        setNGOAndEmbassyInfoIdForLocal(ngoAndEmbassyInformationId);
      })
      .catch((err) => console.log(err));
  };

  const updateCompanyNGOAndEmbassyInformationById = () => {
    // event.preventDefault();
    loanDataService
      .updateCompanyNGOAndEmbassyInformation(
        NGOAndEmbassyInfoIdForLocal,
        editNGOAndEmbassyInfo
      )
      .then((response) => {
        // navigation("/company/1");
        console.log(response);
      })
      .catch((e) => {
        console.log(e);
      });
    // debugger;
  };
  useEffect(() => {
    updateCompanyNGOAndEmbassyInformationById();
  }, []);

  const [NGOAndEmbassyInfo, setNGOAndEmbassyInfo] = useState(
    initialNGOAndEmbassyInfoState
  );
  const initialNGOAndEmbassyState = {
    Id: null,
    AuthRefNo: "",
    AuthDate: "",
    UploadPermisionLetter: "",
    UploadParentCompanyLegalDocument: "",
  };
  const [NGOAndEmbassy, setNGOAndEmbassy] = useState(initialNGOAndEmbassyState);

