import { TextField, IconButton } from "@mui/material";
import { BsPlus } from "react-icons/bs";
import { BiSkipNext } from "react-icons/bi";
import { RiSkipBackMiniFill } from "react-icons/ri";
import CompanyModal from "../Common/CompanyModal";
import { useState } from "react";
import { BiSkipPrevious } from "react-icons/bi";
import LoanIconButton from "../../Common/LoanIconButton";
import LoanTextField from "../../Common/LoanTextField";
const AddCompanyEmployee = ({
  employee,
  newEmployee,
  handleInputChangeEmployee,
  handleBack,
  saveEmployee,
  handleSkip,
  handlePrevios,
}) => {
  const [isCompanyEmployeeSoleModalOpen, setIsCompanyEmployeeSoleModalOpen] =
    useState(false);

  function handleCompanyEmployeeSoleCloseModal() {
    setIsCompanyEmployeeSoleModalOpen(false);
  }
  function handleCompanyEmployeeSoleOpenModal() {
    setIsCompanyEmployeeSoleModalOpen(true);
  }
  return (
    <>
      <div className="flex">
        {/* <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipPrevious />}
          text="Back"
          onClick={handleSkip}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BsPlus />}
          text="Add"
          onClick={handleCompanyEmployeeSoleOpenModal}
        />
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BiSkipNext />}
          text="Skip"
          onClick={handleSkip}
        /> */}

<Link to={`/enterprise/basic-Information/${companyId}`}>
          <LoanIconButton
            style={{ color: "purple" }}
            icon={<BiSkipPrevious />}
            text="Company Basic"
            onClick={handlePrevios}
          />
        </Link>
        <LoanIconButton
          style={{ color: "purple" }}
          icon={<BsPlus />}
          text="Add"
          onClick={handleCompanyAddressOpenModal}
        />
        <Link to={`/contact/${companyId}`}>
          <LoanIconButton
            style={{ color: "purple" }}
            icon={<BiSkipNext />}
            text="Contact Information"
            onClick={handleSkip}
          />
        </Link>
      </div>
      <CompanyModal
        isOpen={isCompanyEmployeeSoleModalOpen}
        onClose={handleCompanyEmployeeSoleCloseModal}
        title="Add company Employee information"
      >
        <div>
          <form onSubmit={saveEmployee}>
            <div className="relative center justify-center flex-1 w-full max-w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              <LoanTextField
                id="BudgetYear"
                label="Budget Year"
                value={employee.BudgetYear}
                onChange={handleInputChangeEmployee}
                name="BudgetYear"
              />

              <LoanTextField
                id="PermanentEmployeeCount"
                label="Permanent Employee Count"
                value={employee.PermanentEmployeeCount}
                onChange={handleInputChangeEmployee}
                name="PermanentEmployeeCount"
              />
              <LoanTextField
                id="TemporaryEmployeeCount"
                label="Temporary Employee Count"
                value={employee.TemporaryEmployeeCount}
                onChange={handleInputChangeEmployee}
                name="TemporaryEmployeeCount"
              />
              <LoanTextField
                id="ContractEmployeeCount"
                label="Contract Employee Count"
                value={employee.ContractEmployeeCount}
                onChange={handleInputChangeEmployee}
                name="ContractEmployeeCount"
              />
              {/* <LoanTextField
                id="SeniorMngtStaffIDTM"
                label="Senior Mngt Staff IDTM"
                value={employee.SeniorMngtStaffIDTM}
                onChange={handleInputChangeEmployee}
                name="SeniorMngtStaffIDTM"
              />
              <LoanTextField
                id="TotalWorkExperiance"
                label="Total Work Experiance"
                value={employee.TotalWorkExperiance}
                onChange={handleInputChangeEmployee}
                name="TotalWorkExperiance"
              />
              <LoanTextField
                id="RelatedWorkExperiance"
                label="Related Work Experiance"
                value={employee.RelatedWorkExperiance}
                onChange={handleInputChangeEmployee}
                name="RelatedWorkExperiance"
              />
              <LoanTextField
                id="UploadCV"
                label="Upload CV"
                value={employee.UploadCV}
                onChange={handleInputChangeEmployee}
                name="UploadCV"
              />
              <LoanTextField
                id="AttachWorkExperience"
                label="Attach Work Experience"
                value={employee.AttachWorkExperience}
                onChange={handleInputChangeEmployee}
                name="AttachWorkExperience"
              /> */}
            </div>
            <div className="flex justify-center pt-4">
              <button
                type="submit"
                className="bg-[purple] hover:bg-purple-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              >
                Save
              </button>
            </div>
          </form>
        </div>
      </CompanyModal>
    </>
  );
};

export default AddCompanyEmployee;
